<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/authority_price')}" class="on">商家权限价格</a>|				</ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>												<th >权限分类</th>															<th>权限子分类</th>								<th>权限子子分类</th>															<th >价格(直接编辑更改)</th>															</tr>						</thead>						<tbody>							<if condition="is_array($menus)">								<volist name="menus" id="vo">									<tr >										<td>{pigcms{$vo.id}</td>																		<td>{pigcms{$vo.name}</td>										<td></td>																			<td></td>																				<td></td>																			</tr>									<if condition="$vo.menu_list">																			<volist name="vo.menu_list" id="vv">											<tr>												<td>{pigcms{$vv.id}</td>																						<td ></td>																							<td>{pigcms{$vv.name}</td>												<td></td>																								<td><if condition="empty($vv['menu_list'])"><input name="price_{pigcms{$vv.id}" data-id="{pigcms{$vv.id}" type="text" class="input-text price" value="{pigcms{$vv.price|floatval}" validate="number:true,min:0"></if></td>																							</tr>											<if condition="$vv.menu_list">																					<volist name="vv.menu_list" id="vd">													<tr>														<td>{pigcms{$vd.id}</td>																																							<td ></td>														<td></td>														<td>{pigcms{$vd.name}</td>																												<td><input name="price_{pigcms{$vd.id}" data-id="{pigcms{$vd.id}" type="text" class="input-text price" value="{pigcms{$vd.price|floatval}" validate="number:true,min:0"></td>																											</tr>																									</volist>											</if>										</volist>									</if>								</volist>															<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script>	$(function(){		$('.price').change(function(){			var id = $(this).data('id');			var price = $(this).val();			var tmp = $(this).val();			$.ajax({				url: '{pigcms{:U('change_authority_price')}',				type: 'POST',				dataType: 'json',				data: {id: id,price:price},				success:function(data){					if(data.error_code){						tmp.val(data.price)						window.top.msg(0,data.msg);					}else{						window.top.msg(1,data.msg);						// alert(data.msg)					}				}			});		});	});</script><include file="Public:footer"/>