<include file="Public:header"/><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>                    <a href="javascript:history.go(-1)" >上一级</a>                    <a href="" class="on">商家统一折扣统计</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Merchant/index')}" method="get">							<input type="hidden" name="c" value="Merchant"/>							<input type="hidden" name="a" value="index"/>							筛选: 							<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商户名称</option>																<option value="account" <if condition="$_GET['searchtype'] eq 'account'">selected="selected"</if>>商户帐号</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                        <font color="#000">日期筛选：</font>                        <input type="text" class="input-text" name="begin_time" style="width:80px;" id="begin_time"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>                                       <input type="text" class="input-text" name="end_time" style="width:80px;" id="end_time" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>						<input type="submit" value="查询" class="button"/>						<b>商家统一折扣优惠总金额：{pigcms{$totalPrice}元</b>　　　　						<b>商家统一折扣优惠总单数：{pigcms{$totalNum}</b>						</form>                        					</td>				</tr>			</table>							<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>								<th>商户名称</th>								<th>联系电话</th>								<th>商家统一折扣优惠总金额</th>								<th>商家统一折扣优惠总单数</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($merchant_list)">								<volist name="merchant_list" id="vo">									<tr>										<td>{pigcms{$vo.mer_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.price|floatval}</td>										<td>{pigcms{$vo.num|intval}</td>										<td class="textcenter">										<a href="{pigcms{:U('Merchant/discount_detail', array('mer_id'=>$vo['mer_id']))}">查看</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="12">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>				</div><script>   var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Merchant/export_info')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>