<include file="Public:header"/><style>.mainnav_title ul a {    padding: 15px 20px;}ul, ol {    margin-bottom: 15px;}.mainnav_title div{	color:#7EBAEF;	float:right;}.mainnav_title a.on div{	color:#C1BEBE;}.info{	line-height:30px;}.table-list thead th {    text-align: center;}.table-list tbody td {    text-align: center;	white-space: nowrap;　　overflow: hidden;　　text-overflow: ellipsis;}button{	    margin: 5px;	padding: 6px;	background-color: rgba(255, 255, 255, 0);	box-sizing: border-box;	border-width: 1px;	border-style: solid;	border-color: rgba(121, 121, 121, 1);	border-radius: 2px;	-moz-box-shadow: none;	-webkit-box-shadow: none;	box-shadow: none;	font-size: 16px;	color: #666666;	cursor: pointer ;}</style>		<div class="mainbox">		<div class="mainnav_title" style="margin-top: 12px;">			<a href="{pigcms{:U('Merchant/discount')}">上一页</a>		</div>			<div style="margin:15px 0 0 0;">				<b>商家ID：</b>{pigcms{$now_merchant.mer_id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>商家名称：</b>{pigcms{$now_merchant.name}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>联系电话：</b>{pigcms{$now_merchant.phone}                <br/>商家统一折扣优惠总金额：{pigcms{$now_merchant.discount_price}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;商家统一折扣优惠总单数：{pigcms{$now_merchant.discount_num}<br/><br/>			</div>			<div class="mainnav_title" style="margin-bottom: 10px;">				<form id="date" method="post" action="{pigcms{:U('Merchant/discount_detail')}" >					<font color="#000">订单：<input type="text" name="order_id" value="{pigcms{$order_id}" placeholder="输入订单号" style="line-height: 18px;height: 18px;">					<input type="hidden" name="mer_id" value="{pigcms{$now_merchant.mer_id}">					<font color="#000">日期筛选：</font>					<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   					<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>					<input type="submit" value="筛选" class="button">				</form>                &nbsp;&nbsp;&nbsp;&nbsp;<a href="{pigcms{:U('Merchant/exportDiscount', array('order_id' => $order_id, 'mer_id' => $now_merchant['mer_id'], 'begin_time' => $begin_time, 'end_time' => $end_time))}" class="" >导出订单</a>			</div>			<div class="table-list">				<table width="100%" cellspacing="0">					<thead>						<tr>							<th>店铺名称</th>							<th>订单号</th>							<th>订单总额</th>							<th>商家统一折扣比例</th>							<th>商家统一折扣优惠总金额</th>							<th>支付时间</th>							<th>操作</th>						</tr>					</thead>					<tbody>						<if condition="$order_list">						<volist name="order_list" id="vo">							<tr>								<td>{pigcms{$vo.name}</td>								<td>{pigcms{$vo.real_orderid}</td>								<td>{pigcms{$vo.price|floatval}</td>								<td>{pigcms{:floatval($vo['discount']/10)} 折</td>								<td>{pigcms{$vo.discount_money|floatval}</td>								<td>{pigcms{$vo.dateline|date="Y-m-d H:i:s",###}</td>								<td>                                    <php> if ($vo['order_from'] == 1) {</php>										<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Foodshop&a=order_detail&order_id={pigcms{$vo.order_id}','查看订单详情',600,460,true,false,false,false,'order_edit',true);"><button>查看详情</button></a>									<php> } elseif ($vo['order_from'] == 0) {</php>                                        <a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Shop&a=order_detail&order_id={pigcms{$vo.order_id}','查看订单详情',600,460,true,false,false,false,'order_edit',true);"><button>查看详情</button></a>									<php> } </php>								</td>							</tr>						</volist>							<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>							<else/>							<tr class="odd"><td class="textcenter red" colspan="12" >暂时还没记录。</td></tr>						</if>					</tbody>				</table>			</div>		</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><script>var url = "{pigcms{$config.site_url}"var export_url = "{pigcms{:U('Merchant/exportDiscount')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>