<include file="Public:header"/><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/index')}" class="on">商户列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/add')}','添加商户',800,560,true,false,false,addbtn,'add',true);">添加商户</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:60%;">						<form action="{pigcms{:U('Merchant/index')}" method="get">							<input type="hidden" name="c" value="Merchant"/>							<input type="hidden" name="a" value="index"/>							筛选: 							<if condition="$admin_area neq 3">							<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>							</if>							<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商户名称</option>																<option value="store" <if condition="$_GET['searchtype'] eq 'store'">selected="selected"</if>>店铺名称</option>																<option value="account" <if condition="$_GET['searchtype'] eq 'account'">selected="selected"</if>>商户帐号</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							商户状态: <select name="searchstatus">								<option value="0" <if condition="$_GET['searchstatus'] eq 0">selected="selected"</if>>正常</option>								<option value="1" <if condition="$_GET['searchstatus'] eq '1'">selected="selected"</if>>待审核</option>								<option value="2" <if condition="$_GET['searchstatus'] eq '2'">selected="selected"</if>>关闭</option>								<option value="3" <if condition="$_GET['searchstatus'] eq '3'">selected="selected"</if>>全部</option>							</select>							排序: <select name="searchorder">								<option value="0" <if condition="$_GET['searchorder'] eq 0">selected="selected"</if>>商家ID</option>								<option value="1" <if condition="$_GET['searchorder'] eq '1'">selected="selected"</if>>商家余额</option>													</select>							<input type="submit" value="查询" class="button"/>													</form>					</td>					<td>						<b>平台商户总余额：{pigcms{$all_money}</b>					</td>										<td>						<a  href="javascript:void(0)" onclick="exports()" class="button" style="float:right;margin-right: 10px;">导出列表</a>					</td>				</tr>			</table>							<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>												<th>商户名称</th>								<if condition="!isset($config['not_o2o_demo'])">								<th>联系电话</th>								</if>								<th>最后登录时间</th>															<th class="textcenter">访问该商户后台</th>															<th class="textcenter">微官网点击数</th>								<th class="textcenter">粉丝数</th>								<th class="textcenter">账户余额</th>								<th>状态</th>								<if condition="C('config.open_extra_price') eq 1">									<th>{pigcms{:C('config.extra_price_alias_name')}结算</th>								</if>																<th class="textcenter">商家账单</th>								<!--th class="textcenter">微店账单</th-->								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($merchant_list)">								<volist name="merchant_list" id="vo">									<tr>										<td>{pigcms{$vo.mer_id}</td>																		<td>{pigcms{$vo.name}</td>										<if condition="!isset($config['not_o2o_demo'])">										<td>{pigcms{$vo.phone}</td>										</if>										<td><if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}<else/>无</if></td>																				<td class="textcenter"><if condition="$vo['status'] eq 1 OR $vo['status'] eq 3"><a href="{pigcms{:U('Merchant/merchant_login',array('mer_id'=>$vo['mer_id']))}" class="__full_screen_link" target="_blank">访问</a><else/><a href="javascript:alert('商户状态不正常，无法访问！请先修改商户状态。');" class="__full_screen_link">访问</a></if></td>																				<td class="textcenter">{pigcms{$vo.hits}</td>										<td class="textcenter">{pigcms{$vo.fans_count}</td>										<td class="textcenter">{pigcms{$vo.money}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><elseif condition="$vo['status'] eq 2"/><font color="red">待审核</font><elseif condition="$vo['status'] eq 3"/><font color="red">欠款</font><else/><font color="red">关闭</font></if></td>										<if condition="C('config.open_extra_price') eq 1">											<td> 商家欠平台{pigcms{$vo.extra_price_pay_for_system}个{pigcms{:C('config.extra_price_alias_name')},即{pigcms{:sprintf("%.2f",$vo['extra_price_pay_for_system']*$vo['extra_price_percent']/100)}元</td>										</if>																				<td class="textcenter"><a href="{pigcms{:U('Bill/merchant_money_list',array('mer_id'=>$vo['mer_id']))}">查看账单</a></td>										<!--td class="textcenter"><a href="{pigcms{:U('Merchant/weidian_order',array('mer_id'=>$vo['mer_id']))}">微店账单</a></td-->										<td class="textcenter">										<a href="{pigcms{:U('Merchant/store',array('mer_id'=>$vo['mer_id']))}">店铺列表</a> | 										<!--<a href="javascript:void(0);" onclick="window.top.show_other_frame('Group','product','mer_id={pigcms{$vo.mer_id}')">{pigcms{$config.group_alias_name}列表</a> | -->										<!--<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/edit',array('mer_id'=>$vo['mer_id'],'frame_show'=>true))}','查看详细信息',520,370,true,false,false,false,'detail',true);">查看</a> | -->										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/edit',array('mer_id'=>$vo['mer_id']))}','编辑商户信息',800,560,true,false,false,editbtn,'edit',true);">编辑</a> | 																				<a href="javascript:void(0);" class="delete_row" parameter="mer_id={pigcms{$vo.mer_id}" url="{pigcms{:U('Merchant/del')}">删除</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="12">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>				</div><script>   var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Merchant/export_info')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>