<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/reply')}" class="on">商户的评论列表</a>|				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:50%;">						<form action="{pigcms{:U('Merchant/reply')}" method="get">							<input type="hidden" name="c" value="Merchant"/>							<input type="hidden" name="a" value="reply"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="m_name" <if condition="$_GET['searchtype'] eq 'm_name'">selected="selected"</if>>商户名称</option>								<option value="s_name" <if condition="$_GET['searchtype'] eq 's_name'">selected="selected"</if>>店铺名称</option>								<option value="nickname" <if condition="$_GET['searchtype'] eq 'nickname'">selected="selected"</if>>用户姓名</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>用户电话</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							<select name="order_type">								<volist name="selectList" id="sel">								<option value="{pigcms{$key}" <if condition="$_GET['order_type'] eq $key">selected="selected"</if>>{pigcms{$sel}</option>								</volist>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th>商户名称</th>								<th>店铺名称</th>								<th>用户名</th>								<th>用户电话</th>								<th>评论类型</th>								<th>评论时间</th>								<th>评分</th>								<th>评论内容</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($reply_list)">								<volist name="reply_list" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td>{pigcms{$vo.m_name}</td>										<td>{pigcms{$vo.s_name}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.phone}</td>										<td><if condition="$vo['order_type'] eq 1">老餐饮<else />{pigcms{$selectList[$vo['order_type']]}</if></td>										<td><if condition="$vo['add_time']">{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}<else/>无</if></td>										<td class="textcenter">{pigcms{$vo.score}</td>										<td style="width:100px">{pigcms{$vo.comment}</td>										<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/replyinfo',array('reply_id'=>$vo['pigcms_id'],'frame_show'=>true))}','查看详细信息',520,470,true,false,false,false,'detail',true);">查看</a> |										<a href="javascript:void(0);" class="delete_row" parameter="reply_id={pigcms{$vo.pigcms_id}" url="{pigcms{:U('Merchant/replydel')}">删除</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>