<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/store_auth')}" <if condition="$status eq 0">class="on"</if>>待审核列表</a>|					<a href="{pigcms{:U('Merchant/store_auth', array('status' => 1))}" <if condition="$status eq 1">class="on"</if>>已审核列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:50%;">						<form action="{pigcms{:U('Merchant/store_auth')}" method="get">							<input type="hidden" name="c" value="Merchant"/>							<input type="hidden" name="a" value="store_auth"/>							<input type="hidden" name="status" value="{pigcms{$status}"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th>店铺名称</th>								<th>联系电话</th>								<th>最后编辑时间</th>								<th>{pigcms{$config.meal_alias_name}</th>								<th>{pigcms{$config.group_alias_name}</th>								<th>{pigcms{$config.shop_alias_name}</th>								<th>店铺审核</th>								<th>{pigcms{$config.shop_alias_name}资质审核状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($store_list)">								<volist name="store_list" id="vo">									<tr>										<td>{pigcms{$vo.store_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td><if condition="$vo['last_time']">{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}<else/>无</if></td>										<td><if condition="$vo['have_meal'] eq 1"><font color="green">开启</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['have_group'] eq 1"><font color="green">开启</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['have_shop'] eq 1"><font color="green">开启</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><elseif condition="$vo['status'] eq 2"/><font color="red">审核中</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['auth'] eq 0">未提交<elseif condition="$vo['auth'] eq 1"/><font color="red">审核中</font><elseif condition="$vo['auth'] eq 2"/><font color="red">已拒绝</font><elseif condition="$vo['auth'] eq 3"/><font color="green">已通过</font><elseif condition="$vo['auth'] eq 4"/><font color="red">审核中</font><elseif condition="$vo['auth'] eq 5"/><font color="red">已驳回</font></if></td>																				<if condition="$vo['auth'] eq 1 OR $vo['auth'] eq 4">											<td class="textcenter">												<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/auth_edit', array('store_id'=>$vo['store_id']))}','资质审核',680,560,true,false,false,editbtn,'edit',true);">审核</a>											</td>										<else/>											<td class="textcenter">												<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/auth_edit', array('store_id'=>$vo['store_id'],'statuss'=>1))}','查看资质审核',680,560,true,false,false);">查看</a>											</td>										</if>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>