<include file="Public:header"/><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Merchant/wx_store')}" class="on">商户列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Merchant/add_wx_store')}','添加微信小程序',600,350,true,false,false,addbtn,'add',true);">添加微信小程序</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:60%;">						<form action="{pigcms{:U('Merchant/index')}" method="get">							<input type="hidden" name="c" value="Merchant"/>							<input type="hidden" name="a" value="wx_store"/>							筛选: 														<input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'nickname'">selected="selected"</if>>小程序昵称</option>																							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;														<input type="submit" value="查询" class="button"/>													</form>					</td>														</tr>			</table>							<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>								<th>一级类目</th>								<th>二级类目</th>								<th>证明材料</th>								<th>头像</th>																<th>介绍</th>								<th>组织代码</th>								<th>审核状态</th>																<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($wx_store)">								<volist name="wx_store" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.first_catid_name}</td>										<td>{pigcms{$vo.second_catid_name}</td>										<td><img style="width:100px;height:60px;" src="{pigcms{$vo.qul_pic}"></td>										<td ><img style="width:100px;height:60px;" src="{pigcms{$vo.head_pic}"></td>										<td >{pigcms{$vo.nickname}</td>										<td >{pigcms{$vo.intro}</td>										<td >{pigcms{$vo.org_code}</td>										<td >{pigcms{$vo.status}</td>																			</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>				</div><script>   var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Merchant/export_info')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>