<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('NewsCategory/index')}" class="on">分类列表</a>|					<if condition="$category">					<a href="{pigcms{:U('NewsCategory/index',array('parentid'=>$category['category_pid'],'level'=>$level))}" class="on">{pigcms{$category.name} - 子分类列表</a>|					</if>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('NewsCategory/cat_add', array('parentid' => $parentid,'level'=>$level))}','添加分类',480,260,true,false,false,addbtn,'add',true);">添加<if condition="$category">子<else />主</if>分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>排序</th>								<th>名称</th>								<th>短标记(url)</th>                                <if condition="$level elt 2 ">								<th>查看子分类</th>                                </if>								<th>状态</th>								<!--<th>店铺不营业时显示状态</th>-->								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.category_id}</td>										<td>{pigcms{$vo.order}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.flag}</td>                                        <if condition="$level elt 2 ">										<td><a href="{pigcms{:U('NewsCategory/index',array('parentid'=>$vo['category_id']))}">查看子分类</a></td>                                        </if>										<td><if condition="$vo['is_display'] eq 1"><font color="green">启用</font><elseif condition="$vo['is_display'] eq 2"/><font color="red">待审核</font><else/><font color="red">关闭</font></if></td>										<!--<td><if condition="$vo['show_method'] eq 0"><font color="green">不显示</font><elseif condition="$vo['show_method'] eq 1"/><font color="red">正常显示</font><else/><font color="red">靠后显示</font></if></td>-->										<td class="textcenter">                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('NewsCategory/cat_edit',array('category_id'=>$vo['category_id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> |                                            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('NewsCategory/cat_edit',array('category_id'=>$vo['category_id'], 'parentid'=>$vo['cat_fid'], 'level'=>$vo['level']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a> |                                            <a href="javascript:void(0);" class="delete_row" parameter="category_id={pigcms{$vo.category_id}" url="{pigcms{:U('NewsCategory/cat_del')}">删除</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>