<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Portal/activity')}" class="on">活动列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_add')}','添加活动',580,450,true,false,false,addbtn,'add',true);">添加活动</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th class="textcenter">标题</th>								<th class="textcenter">活动时间</th>								<th class="textcenter">活动价格</th>								<th class="textcenter">活动人数/报名人数</th>								<th class="textcenter">报名截至时间</th>								<th class="textcenter">分类</th>								<th class="textcenter">负责人</th>								<th class="textcenter">状态</th>								<th class="textcenter">活动介绍</th>								<th class="textcenter">活动报道</th>								<th class="textcenter">赞助商</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($activityList)">								<volist name="activityList" id="vo">									<tr>										<td class="textcenter">{pigcms{:msubstr($vo['title'],0,20)}</td>										<td class="textcenter">{pigcms{$vo.time}</td>										<td class="textcenter">{pigcms{$vo.price}</td>										<td class="textcenter">{pigcms{$vo.number}</td>										<td class="textcenter">{pigcms{$vo.enroll_time|date="Y-m-d H:i:s",###}</td>										<td class="textcenter">{pigcms{$vo.cid}</td>										<td class="textcenter">{pigcms{$vo.leader}</td>										<td class="textcenter"><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_content_edit',array('a_id'=>$vo['a_id'],'content'=>'introduction'))}','编辑活动介绍',800,600,true,false,false,editbtn,'add',true);">编辑</a></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_content_edit',array('a_id'=>$vo['a_id'],'content'=>'report'))}','编辑活动报道',800,600,true,false,false,editbtn,'add',true);">编辑</a></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_content_edit',array('a_id'=>$vo['a_id'],'content'=>'sponsor'))}','编辑赞助商',800,600,true,false,false,editbtn,'add',true);">编辑</a></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_detail',array('a_id'=>$vo['a_id'],'frame_show'=>true))}','查看信息',580,450,true,false,false,false,'detail',true);">查看</a> |<a href="{pigcms{:U('Portal/activity_sign',array('a_id'=>$vo['a_id']))}">报名管理</a> |<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/activity_edit',array('a_id'=>$vo['a_id']))}','编辑分类信息',580,450,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="a_id={pigcms{$vo.a_id}" url="{pigcms{:U('Portal/activity_del')}">删除</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/portal_recomment',array('aid'=>$vo['a_id'],'type'=>1,'frame_show'=>true))}','评论列表',1000,500,true,false,false,false,'detail',true);">评论管理</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="12">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>