<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<if condition="empty($now_category)">						<a href="{pigcms{:U('Portal/article_cat')}" class="on">资讯分类</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/article_cat_add',array('fcid'=>$fcid,'pfcid'=>$pfcid))}','添加主分类',580,450,true,false,false,addbtn,'add',true);">添加资讯分类</a>					<else/>						<a href="{pigcms{:U('Portal/article_cat')}">资讯分类</a>|						<a href="{pigcms{:U('Portal/article_cat',array('fcid'=>$fcid))}" class="on">{pigcms{$now_category.cat_name} - 子分类列表</a>|						<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/article_cat_add',array('fcid'=>$fcid))}','添加子分类',580,450,true,false,false,addbtn,'add',true);">添加子分类</a>					</if>				</ul>			</div>			<if condition="!empty($_GET['cat_fid'])">				<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>			</if>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<if condition="empty($_GET['cat_fid'])">								<col/>								<col/>								<col/>							</if>							<col/>						</colgroup>						<thead>							<tr>								<th>排序</th>								<th>编号</th>								<th>名称</th>								<th>短标记(url)</th>								<if condition="!is_array($now_category)">								 <th>查看子分类</th>								</if>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($catList)">								<volist name="catList" id="vo">									<tr>										<td>{pigcms{$vo.cat_sort}</td>										<td>{pigcms{$vo.cid}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.cat_url}</td>										<if condition="!is_array($now_category)">											<td><a href="{pigcms{:U('Portal/article_cat',array('fcid'=>$vo['cid']))}">查看子分类</a></td>										</if>										<td><if condition="$vo['cat_status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Portal/article_cat_edit',array('cid'=>$vo['cid']))}','编辑分类信息',580,450,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="cid={pigcms{$vo.cid}" url="{pigcms{:U('Portal/article_cat_del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>