<include file="Public:header"/><script src="{pigcms{$static_public}js/layer/layer.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Portal/search_user',array('plate_id'=>$_GET['plate_id']))}" class="on">管理员列表</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>ID</th>								<th>昵称</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<volist name="plateUserList" id="uvo">								<tr>									<td>{pigcms{$uvo.uid}</td>									<td>{pigcms{$uvo.nickname}</td>									<td class="textcenter"><a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$uvo.id}" url="{pigcms{:U('Portal/plate_user_del')}">删除</a></td>								</tr>							</volist>													</tbody>					</table>				</div>			</form>				<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Portal/search_user')}" method="get">							<input type="hidden" name="c" value="Portal"/>							<input type="hidden" name="a" value="search_user"/>							<input type="hidden" name="plate_id" value="{pigcms{$_GET['plate_id']}"/>														筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="uid" <if condition="$_GET['searchtype'] eq 'uid'">selected="selected"</if>>用户ID</option>								<option value="nickname" <if condition="$_GET['searchtype'] eq 'nickname'">selected="selected"</if>>昵称</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>手机号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							<font color="#000">注册日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>							状态:							<select name="status">								<option value="" <if condition="$_GET['status'] eq ' '">selected="selected"</if>>全部</option>								<option value="1" <if condition="$_GET['status'] eq '1'">selected="selected"</if>>正常</option>								<option value="0" <if condition="$_GET['status'] eq '0'">selected="selected"</if>>禁止</option>							</select>							<input type="submit" style="margin-right:20px;" value="查询" class="button"/>						</form>											</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th><a href="{pigcms{:U('User/index',array('sort'=>'uid'))}" style="color:blue;">ID</a></th>								<th>昵称</th>								<th>手机号</th>								<th class="textcenter"><a href="{pigcms{:U('User/index',array('sort'=>'score'))}" style="color:blue;">积分</a></th>								<th class="textcenter">状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($user_list)">								<volist name="user_list" id="vo">									<tr>										<td>{pigcms{$vo.uid}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.phone}</td>										<td class="textcenter">{pigcms{$vo.score_count}</td>										<td class="textcenter"><if condition="$vo['status'] eq 1"><font color="green">正常</font><elseif condition="$vo['status'] eq 2" /><font color="red">未审核</font><else /><font color="red">禁用</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="plateUser({pigcms{$vo.uid},1)">选择</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/><script>	function plateUser(uid){		var plate_id = "{pigcms{$_GET['plate_id']}";		var plateUserUrl = "{pigcms{:U('Portal/plate_user_add')}";		$.post(plateUserUrl,{'uid':uid,'plate_id':plate_id},function(data){			if(data.error == 1){				layer.msg(data.msg,function(){					location.href = location.href;				});			}else{				layer.msg(data.msg)			}		},'json');	}</script>