<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Portal/tieba')}" class="on">贴吧板块</a>				</ul>			</div>			<if condition="!empty($_GET['cat_fid'])">				<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>			</if>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>所属板块</th>								<th class="textcenter">标题</th>								<th class="textcenter">发布时间</th>								<th class="textcenter">回复时间</th>								<th class="textcenter">回复数量</th>								<th class="textcenter">浏览量</th>								<th>精华</th>								<th>置顶</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($tiebaList)">								<volist name="tiebaList" id="vo">									<tr>										<td><if condition="$vo['plate_name'] neq ''"><span class="category">【 {pigcms{$vo.plate_name} 】</span></if></td>										<td>{pigcms{$vo.title}</td>										<td class="textcenter">{pigcms{$vo.add_time|date="Y-m-d H:i:s",###}</td>										<td class="textcenter">{pigcms{$vo.last_time|date="Y-m-d H:i:s",###}</td>										<td class="textcenter">{pigcms{$vo.reply_sum}</td>										<td class="textcenter">{pigcms{$vo.pageviews}</td>										<td><if condition="$vo['is_essence'] eq 1"><font color="green">精华</font><else/><font color="red"></font></if></td>										<td><if condition="$vo['is_top'] eq 1"><font color="green">置顶</font><else/><font color="red"></font></if></td>										<td class="textcenter"><a href="{pigcms{$config.site_url}/portal.php?g=Portal&c=Tieba&a=detail&tie_id={pigcms{$vo.tie_id}" target="_blank">查看</a> | <a href="javascript:void(0);" class="delete_row" parameter="tie_id={pigcms{$vo.tie_id}" url="{pigcms{:U('Portal/tieba_del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>