<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Scenic/scenic_money')}">景区余额存入记录</a>					<a href="{pigcms{:U('Scenic/scenic_money',array('withdraw_status'=>1))}" >待提现景区</a>					<a href="{pigcms{:U('Scenic/scenic_withdraw_list')}" class="on">已提现记录</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Scenic/scenic_withdraw_list')}" method="get">							<input type="hidden" name="c" value="Scenic"/>							<input type="hidden" name="a" value="scenic_withdraw_list"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>景区名称</option>																<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="scenic_id" <if condition="$_GET['searchtype'] eq 'scenic_id'">selected="selected"</if>>景区编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;														<input type="submit" value="查询" class="button"/>							&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>平台景区总余额：{pigcms{$all_money}</b>						</form>					</td>				</tr>			</table>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>景区名称</th>								<th>联系电话</th>								<th>提现金额</th>							</tr>						</thead>						<tbody>							<if condition="$withdraw_list">								<volist name="withdraw_list" id="vo">									<tr>										<td>{pigcms{$vo.scenic_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo['money']/100}元</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>