<include file="Public:header"/><style>.mainnav_title ul a {    padding: 15px 20px;}ul, ol {    margin-bottom: 15px;}.mainnav_title div{	color:#7EBAEF;	float:right;}.mainnav_title a.on div{	color:#C1BEBE;}.info{	line-height:30px;}.table-list thead th {    text-align: center;}.table-list tbody td {    text-align: center;}button{	    margin: 5px;	padding: 6px;	background-color: rgba(255, 255, 255, 0);	box-sizing: border-box;	border-width: 1px;	border-style: solid;	border-color: rgba(121, 121, 121, 1);	border-radius: 2px;	-moz-box-shadow: none;	-webkit-box-shadow: none;	box-shadow: none;	font-size: 16px;	color: #666666;	cursor: pointer ;}</style>		<div class="mainbox">		<div id="nav" class="mainnav_title" style="margin-top: 12px;">			<a href="{pigcms{:U('Scenic/scenic_money')}">上一页</a>		</div>			<div style="margin:15px 0;">				<b>景区ID：</b>{pigcms{$scenic.pigcms_id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>景区名称：</b>{pigcms{$scenic.scenic_name}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>联系电话：</b>{pigcms{$scenic.scenic_phone}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;景区余额：</b>{pigcms{$scenic.now_money}<br/><br/>			</div>			<div id="nav" class="mainnav_title" style="margin-top:5px;">				<ul>					<a href="{pigcms{:U('Scenic/scenic_income_list',array('scenic_id'=>$scenic_id))}">景区余额存入记录</a>					<a href="{pigcms{:U('Scenic/withdraw_info',array('scenic_id'=>$scenic_id,'status'=>0))}" <if condition="empty($status) OR $status eq 0">class="on"</if>>待兑现记录</a>					<a href="{pigcms{:U('Scenic/withdraw_info',array('scenic_id'=>$scenic_id,'status'=>1))}" <if condition=" $status eq 1">class="on"</if>>已兑现记录</a>				</ul>			</div>			<div class="mainnav_title">					<form id="date" method="post" action="{pigcms{:U('Scenic/withdraw_info')}" >						<input type="hidden" name="scenic_id" value="{pigcms{$scenic_id}">						<input type="hidden" name="status" value="{pigcms{$status}">						<font color="#000">日期筛选：</font>						<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$begin_time}" onfocus="WdatePicker({isShowClear:false,readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   						<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$end_time}" onfocus="WdatePicker({isShowClear:false,readOnly:true,dateFmt:'yyyy-MM-dd'})"/>						<input type="submit" value="筛选" class="button">					</form>					</div>							<input type="hidden" id="com_pay_money"name="money" value="">				<input type="hidden" name="scenic_id" value="{pigcms{$scenic_id}">							<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col width="200"/>							<col />							<col/>						</colgroup>						<thead>							<tr>								<th>提现时间</th>								<th>姓名</th>								<th>提现总金额</th>								<th>备注</th>								<if condition="$status eq 0">								<th>操作</th>								<else />								<th>支付方式</th>								</if>							</tr>						</thead>						<tbody>								<if condition="$un_withdraw_list">									<volist name="un_withdraw_list" id="vo">										<tr>											<td>{pigcms{$vo.withdraw_time|date="Y/m/d H:i",###}</td>											<td>{pigcms{$vo.name}</td>											<td>{pigcms{$vo['money']/100}元</td>											<td>{pigcms{$vo.remark}</td>												<if condition="$status eq 0">											<td>												<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Scenic&a=edit_withdraw&id={pigcms{$vo.id}&scenic_id={pigcms{$scenic_id}','编辑',500,300,true,false,false,false,'order_edit',true);"><button>设为以兑现</button></a>												<a href="javascript:void(0);" onclick="window.top.artiframe('/admin.php?g=System&c=Scenic&a=edit_reason&id={pigcms{$vo.id}&scenic_id={pigcms{$scenic_id}','编辑',400,200,true,false,false,false,'order_edit',true);"><button>驳回</button></a>											</td>												<else />												<td><if condition="$vo.online eq 1">企业付款<else />线下付款</if></td>												</if>										</tr>									</volist>									<tr><td class="textcenter pagebar" colspan="5">{pigcms{$pagebar}</td></tr>									<else/>									<tr class="odd"><td class="textcenter red" colspan="5" >该的店铺暂时还没有提现记录。</td></tr>								</if>						</tbody>					</table>				</div>				</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><include file="Public:footer"/>