<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('index')}" class="on">学校列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('school_add')}','添加学校',850,700,true,false,false,addbtn,'store_add',true);">添加学校</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>学校名称</th>								<th>联系人</th>								<th>联系地址</th>								<th>对口学校</th>								<th>类型</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($schoolList)">								<volist name="schoolList" id="vo">									<tr>										<td>{pigcms{$vo.school_id}</td>										<td>{pigcms{$vo.school_name}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo.address}</td>										<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('school_counterpart',array('school_id'=>$vo['school_id']))}','编辑对口学校信息',850,700,true,false,false,editbtn,'edit',true);">对口学校</a></td>										<td>											<if condition="$vo['school_type'] eq 0"><font color="green">公立</font><elseif condition="$vo['school_type'] eq 1"/><font color="red">私立</font></if>										</td>										<td class="textcenter">											<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('school_save',array('school_id'=>$vo['school_id']))}','编辑学校信息',850,700,true,false,false,editbtn,'edit',true);">编辑</a>|											<a href="javascript:void(0);" class="delete_row" parameter="school_id={pigcms{$vo.school_id}" url="{pigcms{:U('school_del')}">删除</a>									  	</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>