<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Send/chanel_msg_list')}" class="on">渠道消息列表</a>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Send/chanel_msg_add')}','添加渠道消息',1000,640,true,false,false,addbtn,'edit',true);">添加渠道消息</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Send/chanel_msg_list')}" method="get">							<input type="hidden" name="c" value="Send"/>							<input type="hidden" name="a" value="chanel_msg_list"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="title" <if condition="$_GET['searchtype'] eq 'title'">selected="selected"</if>>标题</option>								<option value="chanel_id" <if condition="$_GET['searchtype'] eq 'chanel_id'">selected="selected"</if>>消息ID</option>							</select>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>ID</th>								<th>标题</th>								<th>添加时间</th>								<th>最后修改时间</th>								<th>查看二维码</th>								<th class="textcenter">状态(点击改变)</th>								<th class="textcenter">编辑</th>							</tr>						</thead>						<tbody>							<if condition="is_array($chanel_list)">								<volist name="chanel_list" id="vo">									<tr>										<td>{pigcms{$vo.chanel_id}</td>										<td>{pigcms{$vo.title}</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td>{pigcms{$vo.last_time|date='Y-m-d H:i:s',###}</td>										<td><a href="{pigcms{$config.site_url}/index.php?g=Index&c=Recognition&a=see_qrcode&type=chanel&id={pigcms{$vo.chanel_id}" class="see_qrcode">查看二维码</a></td>										<td class="textcenter"><a href="javascript:void(0)" onclick="changeStatus(this,{pigcms{$vo.chanel_id},{pigcms{$vo.status})"><if condition="$vo['status'] eq 1"><font color="green">正常</font><elseif condition="$vo['status'] eq 2" /><font color="red">未审核</font><else /><font color="red">禁用</font></if></a></td>										<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Send/chanel_msg_edit',array('chanel_id'=>$vo['chanel_id']))}','编辑实体卡信息',1000,640,true,false,false,editbtn,'edit',true);">编辑</a>&nbsp;&nbsp;										<a href="javascript:void(0);" class="delete_row"  url="{pigcms{:U('Send/delete_chanel_msg_list',array('chanel_id'=>$vo['chanel_id']))}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="7">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/jquery.artDialog.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/artdialog/iframeTools.js"></script><script type="text/javascript">	$(function(){		$('#indexsort_edit_btn').click(function(){			$(this).prop('disabled',true).html('提交中...');			$.post("/merchant.php?g=Merchant&c=Config&a=merchant_indexsort",{group_indexsort:$('#group_indexsort').val(),indexsort_groupid:$('#indexsort_groupid').val()},function(result){				alert('处理完成！正在刷新页面。');				window.location.href = window.location.href;			});		});		$('.see_qrcode').click(function(){			art.dialog.open($(this).attr('href'),{				init: function(){					var iframe = this.iframe.contentWindow;					window.top.art.dialog.data('iframe_handle',iframe);				},				id: 'handle',				title:'查看渠道二维码',				padding: 0,				width: 430,				height: 433,				lock: true,				resize: false,				background:'black',				button: null,				fixed: false,				close: null,				left: '50%',				top: '38.2%',				opacity:'0.4'			});			return false;		});	});	function changeStatus(obj,id,status){		$.post("{pigcms{:U('Send/change_chanel_status')}", {chanel_id: id,status:status}, function(data, textStatus, xhr) {			if(data.status){				   window.location.reload();			}		});	}	</script><include file="Public:footer"/>