<include file="Public:header"/>		<link rel="stylesheet" type="text/css" href="{pigcms{$static_path}css/workorder.css" />		<div class="mainbox">			<form name="myform" id="myform" action="" method="post" enctype="multipart/form-data">			<div id="content" style="width:810px;float: left;">			<div class="uitopg">			<div class="module-wrap">				<div class="bordersection mb20">					<ul class="bubble-wrap" id="communication_record">						<volist name="message_log" id="row">						<if condition="$row['type'] eq 1">						<li>							<div class="bubble-main fr">								<p class="bubble-content">{pigcms{$row['message']}</p>								<div class="arrow-right"></div>							</div>							<span class="tips right">{pigcms{$row.dateline|date='Y-m-d H:i:s',###}</span>						</li>						<else />						<li>							<div class="reply_user left mg-r-10"><img src="{pigcms{$user['avatar']}" style="width: 70px;height:70px;border-radius: 60px;"> </div>							<p class="">{pigcms{$user['nickname']}</p>							<div class="bubble-main left">								<p class="bubble-content" style="color:#444;white-space:pre-wrap">{pigcms{$row['message']}</p>								<div class="arrow-left"></div>							</div>							<span class="tips left-floating">{pigcms{$row.dateline|date='Y-m-d H:i:s',###}</span>						</li>						</if>						</volist>					</ul>				</div>				<div class="workorder-comment-block">					<h2 class="section-title">留言/回复 </h2>					<input type="hidden" id="openid" value="{pigcms{$user['openid']}">					<div class="textarea-autofit">						<textarea id="j-comment-info" maxlength="1000" placeholder="此处限定1000字符" class="validate['required']" name="feedback" style="padding: 0"></textarea>					</div>					<div class="toolbar">						<div class="cc">							<input class="btn-fmsubmiti y-btn mr10" id="myform_btn" type="button" name="reply_form_customer" value="提交">						</div>					</div>				</div>				</div>				</div>			</div>			</form>		</div><script type="text/javascript">
$(document).ready(function(){	var flag = false;	$('#myform_btn').click(function(){		var message = $('#j-comment-info').val(), openid = $('#openid').val();		if (flag) return false;		if (message.length < 1) return false;		flag = true;		$.post('{pigcms{:U("Send/sendUser")}', {'openid':openid, 'content':message}, function(response){			flag = false;			if (response.errcode) {				alert(response.errmsg);			} else {				$('#j-comment-info').val('')				var html = '<li>';				html += '<div class="bubble-main fr">';				html += '<p class="bubble-content">' + message + '</p>';				html += '<div class="arrow-right"></div>';				html += '</div>';				html += '<span class="tips right">' + response.time + '</span>';				html += '</li>';				$('#communication_record').append(html);			}		}, 'json'); 	});});</script><include file="Public:footer"/>