<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<a href="{pigcms{:U('Service/authentication')}" <if condition="$status eq 1">class="on"</if>>待审核列表</a>|			<a href="{pigcms{:U('Service/authentication',array('status'=>2))}" <if condition="$status eq 2">class="on"</if>>已审核列表</a>|			<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/authentication_config')}','添加认证配置',580,420,true,false,false,false,'detail',true);">认证配置</a>					</ul>	</div>	<if condition="!empty($_GET['cat_fid'])">		<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>	</if>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup>					<col/>					<col/>					<col/>					<col/>					<col/>					<col/>				</colgroup>				<thead>					<tr>						<th>编号</th>						<th>用户ID</th>						<th>审核内容</th>						<th>状态</th>					 	<th>认证时间</th>					 	<if condition="$status eq 2"><th>审核时间</th></if>						<th class="textcenter">操作</th>					</tr>				</thead>				<tbody>					<if condition="is_array($authentication_list)">						<volist name="authentication_list" id="vo">							<tr>								<td>{pigcms{$vo.authentication_id}</td>								<td>{pigcms{$vo.uid}</td>								<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/authentication_check',array('id'=>$vo['authentication_id'],'status'=>1))}','查看实名认证',680,560,true,false,false);">查看</a></td>								<if condition="$vo['authentication_status'] eq 1">									<td style="color:blue">待审核</td>								<elseif condition="$vo['authentication_status'] eq 2"/>									<td style="color:green">审核通过</td>								<else/>									<td style="color:red">审核不通过</td>								</if>								<td>{pigcms{$vo.authentication_time|date="Y-m-d H:i:s",###}</td>								<if condition="$status eq 2"><td><if condition="$vo['examine_time']">{pigcms{$vo.examine_time|date="Y-m-d H:i:s",###}</if></td></if>								<if condition="$vo['authentication_status'] eq 1">									<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/authentication_check',array('id'=>$vo['authentication_id']))}','审核服务认证',680,560,true,false,false,editbtn,'edit',true);">审核</a>									</td>								<else/>									<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/authentication_check',array('id'=>$vo['authentication_id'],'status'=>1))}','查看服务认证',680,560,true,false,false);">查看</a>									</td>								</if>							</tr>						</volist>						<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>					<else/>						<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>					</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>