<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Service/cat_field',array('cid'=>$now_category['cid']))}" class="on">字段列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_field_add',array('cid'=>$now_category['cid']))}','添加字段',600,400,true,false,false,addbtn,'add',true);">添加字段</a><if condition="$now_category['subdir'] gt 1 AND empty($now_category['cat_field']) AND !$f_empty_cat_field"> | <a href="{pigcms{:U('Service/fieldInherit',array('cid'=>$now_category['cid'],'pcid'=>$now_category['fcid']))}" class="on">继承上级目录字段</a></if>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<!--col/-->							<col/>							<col/>							<col/>							<!--col/-->							<!--col width="180" align="center"/-->						</colgroup>						<thead>							<tr>								<!--th>排序</th-->								<th>名称</th>								<th>类型</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<tr><td class="textcenter red" colspan="5">添加字段时必须要一个地图类型来确定用户坐标，否则服务商无法根据服务范围进行接单</td></tr>							<if condition="is_array($now_category['cat_field'])">								<volist name="now_category['cat_field']" id="vo">									<tr>										<!--td>{pigcms{$vo.sort}</td-->										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$inputTypeArr[$vo['type']]['tname']}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_field_edit',array('cid'=>$now_category['cid'],eid=>$key))}','编辑字段',600,400,true,false,false,editbtn,'edit',true);">编辑</a>&nbsp;|&nbsp;										<a href="javascript:void(0);" class="delete_row" parameter="cid={pigcms{$now_category.cid}&name={pigcms{$vo.name}" url="{pigcms{:U('Service/cat_field_del')}">删除</a>										</td>										<!--td><if condition="$vo['status'] eq 1"><font color="green">显示</font><else/><font color="red">隐藏</font></if></td-->										<!--td class="textcenter"><a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.cat_id}" url="{pigcms{:U('Service/cat_del')}">删除</a></td-->									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="5">表单填写项 列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>