<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<if condition="empty($now_category)">				<a href="{pigcms{:U('Service/index')}" class="on">分类列表</a>|				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_add',array('fcid'=>$fcid,'pfcid'=>$pfcid))}','添加主分类',580,450,true,false,false,addbtn,'add',true);">添加主分类</a>				//<a href="{pigcms{:U('Config/index',array('galias'=>'service','header'=>'Service/config'))}">跑腿配置</a>			<else/>				<a href="{pigcms{:U('Service/index')}">分类列表</a>|				<a href="{pigcms{:U('Service/index',array('fcid'=>$fcid))}" class="on">{pigcms{$now_category.cat_name} - 子分类列表</a>|				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_add',array('fcid'=>$fcid))}','添加子分类',580,450,true,false,false,addbtn,'add',true);">添加子分类</a> | 					<a href="{pigcms{:U('Service/index',array('fcid'=>0,'pfcid'=>0))}">返回上级目录</a>			</if>		</ul>	</div>	<if condition="!empty($_GET['cat_fid'])">		<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>	</if>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup>					<col/>					<col/>					<col/>					<if condition="$_GET['fcid'] lt 1">						<col/>					</if>				 	<if condition="$_GET['fcid'] gt 0">				 		<col/>				 	</if>					<col/>					<col/>				</colgroup>				<thead>					<tr>						<th>排序</th>						<th>编号</th>						<th>名称</th>						<if condition="$_GET['fcid'] lt 1">							<th>查看子分类</th>						</if>					 	<if condition="$_GET['fcid'] gt 0">					 		<th>发布信息需填项设置</th>					 	</if>						<th>状态</th>						<th class="textcenter">操作</th>					</tr>				</thead>				<tbody>					<if condition="is_array($category_list)">						<volist name="category_list" id="vo">							<tr>								<td>{pigcms{$vo.cat_sort}</td>								<td>{pigcms{$vo.cid}</td>								<td>{pigcms{$vo.cat_name}</td>								<if condition="$_GET['fcid'] lt 1">									<td><a href="{pigcms{:U('Service/index',array('fcid'=>$vo['cid'],'pfcid'=>$vo['fcid']))}">查看子分类</a></td>								</if>								<if condition="$_GET['fcid'] gt 0">									<if condition="$vo.type eq 2">										<td>特殊分类(帮我买)</td>									<elseif condition="$vo.type eq 3"/>										<td>特殊分类(帮我送)</td>									<else/>										<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_field',array('cid'=>$vo['cid']))}','表单填写项属性字段',580,420,true,false,false,false,'detail',true);">添加/查看设置</a></td>									</if>							 									 	</if>								<td><if condition="$vo['cat_status'] eq 1"><font color="green">启用</font><elseif condition="$vo['cat_status'] eq 2"/><font color="red">待审核</font><else/><font color="red">关闭</font></if></td>								<td class="textcenter">									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_edit',array('cid'=>$vo['cid'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> | 									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/cat_edit',array('cid'=>$vo['cid']))}','编辑分类信息',580,450,true,false,false,editbtn,'add',true);">编辑</a> | 									<a href="javascript:void(0);" class="delete_row" parameter="cid={pigcms{$vo.cid}" url="{pigcms{:U('Service/cat_del')}">删除</a>								</td>							</tr>						</volist>						<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>					<else/>						<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>					</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>