<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Service/publish_list')}" class="on">服务列表</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>订单编号</th>								<th>用户</th>								<th>服务商</th>								<th>分类</th>								<th>价格</th>								<th>时间</th>								<th>状态</th>								<th class="textcenter">订单详情</th>								<th class="textcenter">留言记录</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($publishList)">								<volist name="publishList" id="vo">									<tr>										<td>{pigcms{$vo.order_sn}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.provider_name}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.price}</td>										<td>{pigcms{$vo.add_time|date="Y-m-d H:i:s",###}</td>										<td>											<if condition="$vo.status eq 1">待支付                                            <elseif condition="$vo.status eq 2"/>已支付待服务                                            <elseif condition="$vo.status eq 3"/>服务已完成											<elseif condition="$vo.status eq 4"/>订单完成											<elseif condition="$vo.status eq 5"/>用户申请退款											<elseif condition="$vo.status eq 6"/>退款成功											<elseif condition="$vo.status eq 7"/>订单完成											<elseif condition="$vo.status eq 8"/>等待取货											<elseif condition="$vo.status eq 9"/>配送中                                            <elseif condition="$vo.status eq 10"/>用户已取消                                            <elseif condition="$vo.status eq 11"/>服务过期                                            <elseif condition="$vo.status eq 12"/>过期退款                                            <elseif condition="$vo.status eq 13"/>退款失败											</if>										</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/offer_info',array('publish_id'=>$vo['publish_id'],'offer_id'=>$vo['offer_id'],'frame_show'=>true))}','查看订单信息',600,600,true,false,false,false,'detail',true);">查看</a></td>										<td class="textcenter">											<if condition="$vo['catgory_type'] eq 1">												<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Service/offer_message',array('publish_id'=>$vo['publish_id'],'offer_id'=>$vo['offer_id'],'frame_show'=>true))}','查看订单信息',600,600,true,false,false,false,'detail',true);">查看</a>											<else/>												--											</if>																					</td>										<td class="textcenter">											<if condition="$vo.status eq 2">												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.publish_id}")'>服务退款</a> 											<elseif condition="$vo.status eq 3"/>												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.publish_id}")'>服务退款</a> | 												<!-- <a href="javascript:void(0);" onclick='offerConfirm("{pigcms{$vo.offer_id}")'>完成服务</a> -->											<elseif condition="$vo.status eq 4"/>											订单完成											<elseif condition="$vo.status eq 5"/>												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.publish_id}")'>服务退款</a> | 												<!-- <a href="javascript:void(0);" onclick='offerConfirm("{pigcms{$vo.offer_id}")'>完成服务</a> -->											<elseif condition="$vo.status eq 6"/>											退款成功											<elseif condition="$vo.status eq 7"/>											订单完成											<else/>											--											</if>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>				<script>            function refundReply(publish_id){                var refund_reply_url = "{pigcms{:U('Service/publish_refund_reply')}";                var status = 6;                $.post(refund_reply_url,{'publish_id':publish_id,'status':status},function(data){                    if(data.error == 1){                        alert(data.msg);                        location.reload();                    }else{                        alert(data.msg);                    }                },'json')            }            function offerConfirm(offer_id){		        var user_confirm_service_url = "{pigcms{:U('Service/confirm_service')}";		        $.post(user_confirm_service_url,{'offer_id':offer_id},function(data){		            if(data.error == 1){		                alert(data.msg);		                location.reload();		            }else{		                alert(data.msg);		            }		        },'json');		    }        </script><include file="Public:footer"/>