<include file="Public:header"/><script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('setAccountDeposit/money_list')}" class="on">平台流水</a>|									</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:60%;">						<form action="{pigcms{:U('setAccountDeposit/money_list')}" method="get">							<input type="hidden" name="c" value="setAccountDeposit"/>							<input type="hidden" name="a" value="money_list"/>							筛选: 							<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>							<input type="submit" value="查询" class="button"/>													</form>					</td>										<!--td>						<a  href="javascript:void(0)" onclick="exports()" class="button" style="float:right;margin-right: 10px;">导出列表</a>					</td-->				</tr>			</table>							<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col><col><col width="240" align="center"> </colgroup>						<thead>							<tr>								<th>商户订单号（支付订单）</th>												<th>云账户订单号</th>								<th>初始金额</th>								<th>变更金额</th>								<th>变更时间</th>								<th class="textcenter">描述</th>															</tr>						</thead>						<tbody>							<if condition="is_array($list)">								<volist name="list" id="vo">									<tr>										<td>{pigcms{$vo.bizOrderNo}</td>																		<td>{pigcms{$vo.tradeNo}</td>										<td>{pigcms{$vo['oriAmount']/100 }</td>										<td>{pigcms{$vo['chgAmount']/100  }</td>										<td>{pigcms{$vo.changeTime  }</td>																	<td class="textcenter">{pigcms{$vo.accountSetName }</td>																			</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="6">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>				</div><script>   var url = "{pigcms{$config.site_url}"    var export_url = "{pigcms{:U('Merchant/export_info')}"</script><script type="text/javascript" src="{pigcms{$static_public}js/export.js"> </script><include file="Public:footer"/>