<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Shop/discount')}" class="on">选择区域</a>				</ul>			</div>			<form name="myform" action="{pigcms{:U('Shop/area', array('id' => $_GET['id']))}" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>                                <th><label><input type="checkbox" id="all">&nbsp;&nbsp;全选</label></th>								<th>编号</th>							</tr>						</thead>						<tbody>							<if condition="is_array($area_list)">								<volist name="area_list" id="vo">									<tr>                                        <td><label><input type="checkbox" name="areaIds[]" id="check_{pigcms{$vo['area_id']}" data-id="{pigcms{$vo['area_id']}" <if condition="$vo['select']">checked</if> value="{pigcms{$vo['area_id']}">&nbsp;&nbsp;{pigcms{$vo.area_name}</label></td>										<td>{pigcms{$vo.area_id}</td>									</tr>                                    <php> if ($vo['son_list']) {</php>                                    <tbody class="son_{pigcms{$vo.area_id}">                                    <volist name="vo['son_list']" id="son">                                    <tr style="background: #f7f6f1">                                        <td><label><input type="checkbox" name="areaIds[]" id="check_{pigcms{$son['area_id']}" data-id="{pigcms{$son['area_id']}" <if condition="$son['select']">checked</if> value="{pigcms{$vo['area_id']}-{pigcms{$son['area_id']}">&nbsp;&nbsp;|--------{pigcms{$son.area_name}</label></td>                                        <td>{pigcms{$son.area_id}</td>                                    </tr>                                    <php> if ($son['son_list']) {</php>                                    <tbody class="son_{pigcms{$vo.area_id} son_{pigcms{$son.area_id}">                                    <volist name="son['son_list']" id="last">                                    <tr style="background: #e8e8e3">                                        <td><label><input type="checkbox" name="areaIds[]" id="check_{pigcms{$last['area_id']}" data-id="{pigcms{$last['area_id']}" <if condition="$last['select']">checked</if> value="{pigcms{$vo['area_id']}-{pigcms{$son['area_id']}-{pigcms{$last['area_id']}">&nbsp;&nbsp;|--------------------{pigcms{$last.area_name}</label></td>                                        <td>{pigcms{$last.area_id}</td>                                    </tr>                                    </volist>                                    </tbody>                                    <php> } </php>                                    </volist>                                    </tbody>                                    <php> } </php>                                    								</volist>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>                <div class="btn">                    <input type="submit"  name="dosubmit" value="提交" class="button" />                </div>				</div>			</form>		</div><script type="text/javascript">
$(document).ready(function(){    $('#all').click(function(){        if ($(this).is(':checked')) {            $('input[type="checkbox"]').attr('checked', true);        } else {            $('input[type="checkbox"]').attr('checked', false);        }    });    $('input[type="checkbox"]').click(function(){        var id = $(this).data('id');        var idStr = $(this).val();        var idArr = idStr.split('-');                if ($(this).is(':checked')) {            if (idArr.length > 1) {                for (var i = 0; i < idArr.length - 1; i++) {                    $('#check_' + idArr[i]).attr('checked', true);                }            }            $('.son_' + id).find('input[type="checkbox"]').attr('checked', true);        } else {                        $('.son_' + id).find('input[type="checkbox"]').attr('checked', false);        }    });});</script><include file="Public:footer"/>