<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="javascript:void(0);" class="on">快店优惠列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_add')}','添加优惠',480,380,true,false,false,addbtn,'add',true);">添加优惠</a>				</ul>			</div>            <table class="search_table" width="100%">                <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Shop/discount')}" method="get">                            <input type="hidden" name="c" value="Shop"/>                            <input type="hidden" name="a" value="discount"/>                            <if condition="$admin_area neq 3">                            <div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>                            </if>　　　                            优惠类型: <select name="searchstatus">                                <option value="-1" <if condition="$_GET['searchstatus'] eq -1">selected="selected"</if>>全部</option>                                <option value="0" <if condition="$_GET['searchstatus'] eq 0">selected="selected"</if>>新单</option>                                <option value="1" <if condition="$_GET['searchstatus'] eq '1'">selected="selected"</if>>满减</option>                                <option value="2" <if condition="$_GET['searchstatus'] eq '2'">selected="selected"</if>>配送</option>                            </select>　　　                            排序:<select name="sort">                                <option value="0" <if condition="$_GET['sort'] eq 0">selected="selected"</if>>最新添加</option>                                <option value="1" <if condition="$_GET['sort'] eq '1'">selected="selected"</if>>最早添加</option>                            </select>                           &nbsp; &nbsp;添加日期筛选：                            <input type="text" class="input-text" name="begin_time" style="width:80px;" id="begin_time"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>                            <input type="text" class="input-text" name="end_time" style="width:80px;" id="end_time" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>                            <input type="submit" value="查询" class="button"/>                        </form>                    </td>                </tr>            </table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>类别</th>								<th>满足金额</th>								<th>优惠金额</th>								<th>平台补贴金额</th>                                <th>添加时间</th>								<th>商家补贴金额</th>								<th>使用状态</th>								<th>使用区域</th>								<th>使用商家</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($discount_list)">								<volist name="discount_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td><if condition="$vo['type'] eq 0">新单<elseif condition="$vo['type'] eq 1" />满减<else />配送</if></td>										<td>{pigcms{$vo.full_money}</td>										<td>{pigcms{$vo.reduce_money}</td>										<td>{pigcms{$vo.plat_money}</td>                                        <if condition="$vo.create_time eq 0">                                            <td style="margin-left: 50px;">—</td>                                        <else/>                                            <td>{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</td>                                        </if>										<td>{pigcms{$vo.merchant_money}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['use_area'] eq 1"><a href="{pigcms{:U('Shop/area',array('id' => $vo['id']))}">指定区域</a><else/><font color="green">所有区域可用</font></if></td>										<td><if condition="$vo['use_type'] eq 1"><a href="{pigcms{:U('Shop/merchant',array('id' => $vo['id']))}">指定商家</a><else/><font color="green">全部商家</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_edit',array('id' => $vo['id'], 'frame_show'=>true))}','查看优惠信息',480,380,true,false,false,false,'detail',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/discount_edit',array('id' => $vo['id']))}','编辑优惠信息',480,380,true,false,false,editbtn,'edit',true);">编辑</a><if condition="$vo.status eq 0">| <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('delete_discount')}">删除</a></if>										</td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>        <script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script><include file="Public:footer"/>