<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Shop/index')}" <if condition="!$category">class="on"</if>>分类列表</a>|					<if condition="$category">					<a href="{pigcms{:U('Shop/index',array('parentid'=>$category['cat_id']))}" class="on">{pigcms{$category.cat_name} - 子分类列表</a>|					</if>					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_add', array('parentid' => $parentid))}','添加分类',480,260,true,false,false,addbtn,'add',true);">添加<if condition="$category">子<else />主</if>分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>排序</th>								<th>名称</th>								<th>短标记(url)</th>								<if condition="empty($parentid)">									<th>查看子分类</th>								<else/>									<th>表单填写项</th>								</if>								<th>状态</th>								<th>店铺不营业时显示状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.cat_id}</td>										<td>{pigcms{$vo.cat_sort}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.cat_url}</td>										<if condition="empty($parentid)">											<td><a href="{pigcms{:U('Shop/index',array('parentid'=>$vo['cat_id']))}">查看子分类</a></td>										<else/>											<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('cue_field',array('cat_id'=>$vo['cat_id']))}','表单填写项',580,420,true,false,false,false,'detail',true);">表单填写项</a></td>										</if>										<td><if condition="$vo['cat_status'] eq 1"><font color="green">启用</font><elseif condition="$vo['cat_status'] eq 2"/><font color="red">待审核</font><else/><font color="red">关闭</font></if></td>										<td><if condition="$vo['show_method'] eq 0"><font color="green">不显示</font><elseif condition="$vo['show_method'] eq 1"/><font color="red">正常显示</font><else/><font color="red">靠后显示</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_edit',array('cat_id'=>$vo['cat_id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/cat_edit',array('cat_id'=>$vo['cat_id'], 'parentid'=>$vo['cat_fid']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="cat_id={pigcms{$vo.cat_id}" url="{pigcms{:U('Shop/cat_del')}">删除</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>