<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Shop/discount')}" class="on">指定商户</a>|				</ul>			</div>            <table class="search_table" width="100%">                <tr>                    <td style="width:60%;">                        <form action="{pigcms{:U('Shop/merchant', array('id' => $did))}" method="get">                            <input type="hidden" name="c" value="Shop"/>                            <input type="hidden" name="id" value="{pigcms{$did}"/>                            <input type="hidden" name="a" value="merchant"/>                            <select name="status">                                <option value="0" <if condition="$status eq 0">selected="selected"</if>>全部</option>                                <option value="1" <if condition="$status eq 1">selected="selected"</if>>已上线</option>                                <option value="2" <if condition="$status eq 2">selected="selected"</if>>未上线</option>                            </select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                            <select name="searchtype">                                <option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商户名称</option>                                <option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>                                <option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>                            </select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                            <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>                            <input type="submit" value="查询" class="button"/>                            <input type="button" value="批量上线" id="online" class="button"/>&nbsp;&nbsp;&nbsp;&nbsp;                            <input type="button" value="批量下线" id="offline" class="button"/>&nbsp;&nbsp;&nbsp;&nbsp;                        </form>                    </td>                </tr>            </table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th><input type="checkbox" id="all"></th>								<th>编号</th>								<th>商户名称</th>								<th>联系电话</th>								<th>状态</th>								<th>操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($merchantList)">								<volist name="merchantList" id="vo">									<tr>										<td><input type="checkbox" name="merIds[]" value="{pigcms{$vo.mer_id}" <if condition="!empty($vo['did'])">checked</if>></td>										<td>{pigcms{$vo.mer_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><elseif condition="$vo['status'] eq 2"/><font color="red">待审核</font><elseif condition="$vo['status'] eq 3"/><font color="red">欠款</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter" data-id="{pigcms{$vo.mer_id}">                                        <span class="cb-enable"><label class="cb-enable <if condition="!empty($vo['did'])">selected</if>"><span>已上线</span><input type="radio" name="selectMerchant{pigcms{$vo['mer_id']}" value="1" <if condition="!empty($vo['did'])">checked="checked"</if> /></label></span>                                        <span class="cb-disable"><label class="cb-disable <if condition="empty($vo['did'])">selected</if>"><span>未上线</span><input type="radio" name="selectMerchant{pigcms{$vo['mer_id']}" value="0" <if condition="empty($vo['did'])">checked="checked"</if>/></label></span>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="12">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="12">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script type="text/javascript">$(document).ready(function(){    $('#all').click(function(){        if ($(this).is(':checked')) {            $('input[type="checkbox"]').attr('checked', true);        } else {            $('input[type="checkbox"]').attr('checked', false);        }    });    var did = '{pigcms{$did}';    $('input[type="radio"]').click(function(){        var merIds = [], mer_id = $(this).parents('td').data('id'), obj = $(this).parents('tr').find('input[type="checkbox"]');        var type = $(this).val();        merIds.push(mer_id);        $.post('{pigcms{:U("Shop/change")}', {'did':did, 'type':type,'merIds':merIds}, function(res){            if (res.status == 1) {                if (type == 1) {                    obj.attr('checked', true);                } else {                    obj.attr('checked', false);                }            } else {                alert(res.info);            }        }, 'json');    });    $('#online').click(function(){        var merIds = [];        $("input[name='merIds[]']:checked").each(function(){            merIds.push($(this).val());        });        $.post('{pigcms{:U("Shop/change")}', {'did':did, 'type':1,'merIds':merIds}, function(res){            if (res.status == 1) {                location.reload();            } else {                alert(res.info);            }        }, 'json');    });    $('#offline').click(function(){        var merIds = [];        $("input[name='merIds[]']:checked").each(function(){            merIds.push($(this).val());        });        $.post('{pigcms{:U("Shop/change")}', {'did':did, 'type':0,'merIds':merIds}, function(res){            if (res.status == 1) {                location.reload();            } else {                alert(res.info);            }        }, 'json');    });    });</script><include file="Public:footer"/>