<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="javascript:history.go(-1)" >上一级</a>					<a href="" class="on">{pigcms{$config['shop_alias_name']}补贴统计</a>									</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Shop/merchant_reduce')}" method="get">							<input type="hidden" name="c" value="Shop"/>							<input type="hidden" name="a" value="merchant_reduce"/>							<input type="hidden" name="area_id" value="{pigcms{$_GET['area_id']}"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="name" <if condition="$_GET['searchtype'] eq 'name'">selected="selected"</if>>商户名称</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>联系电话</option>								<option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;														<div style="display:inline"><font color="#000">时间筛选 ：</font></div>							<input type="text" class="input" name="begin_time" id="d4311"  value="{pigcms{$begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>&nbsp;&nbsp;&nbsp;							<input type="text" class="input" name="end_time" id="d4312" value="{pigcms{$end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>&nbsp;&nbsp;&nbsp;												<input type="submit" value="查询" class="button"/>							&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>平台补贴金额：{pigcms{$balance_reduce}</b>&nbsp;&nbsp;&nbsp;&nbsp;<b>							&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>商家补贴金额：{pigcms{$merchant_reduce}</b>&nbsp;&nbsp;&nbsp;&nbsp;<b>						</form>					</td>				</tr>			</table>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>商户名称</th>								<th>联系电话</th>								<th>商家补贴的金额</th>								<th>平台补贴的金额</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>												<if condition="$list">								<volist name="list" id="vo">									<tr>										<td>{pigcms{$vo.mer_id}</td>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.phone}</td>										<td>{pigcms{$vo['merchant_reduce']|floatval}</td>										<td>{pigcms{$vo['balance_reduce']|floatval}</td>										<td class="textcenter"><a href="{pigcms{:U('Shop/reduce_list',array('mer_id'=>$vo['mer_id'], 'area_id' => $_GET['area_id'], 'begin_time' => $_GET['begin_time'], 'end_time' => $_GET['end_time']))}" >查看</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>