<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Shop/shop')}" class="on">{pigcms{$config['deliver_name']}店铺列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Shop/shop')}" method="get">							<input type="hidden" name="c" value="Shop"/>							<input type="hidden" name="a" value="shop"/>							店铺名: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>商家名称</th>								<th>店铺名称</th>								<th>独立配送设置</th>								<th>服务范围</th>								<th>商家电话</th>								<th>店铺电话</th>								<th>创建时间</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.merchant_name}</td>										<td>{pigcms{$vo.store_name}</td>										<td><if condition="$vo['s_is_open_own'] eq 1"><b style="color:red">开启</b><else />关闭</if></td>										<td><if condition="$vo['delivery_range_type'] eq 1">自定义<elseif condition="$vo['delivery_radius'] gt 0" />{pigcms{$vo.delivery_radius|floatval}公里<else /><b style="color:red">未设置</b></if></td>										<td>{pigcms{$vo.merchant_phone}</td>										<td>{pigcms{$vo.store_phone}</td>										<td>{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/shop_edit',array('store_id' => $vo['store_id']))}','修改服务距离',680,480,true,false,false,editbtn,'edit',true);">设置店铺配送</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>