<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Shop/shop_list')}" class="on">{pigcms{$config['shop_alias_name']}店铺列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Shop/shop_list')}" method="get">							<input type="hidden" name="c" value="Shop"/>							<input type="hidden" name="a" value="shop_list"/>							店铺名: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							&nbsp;&nbsp;&nbsp;&nbsp;店铺状态：<select name="status">								<option value="0" <if condition="$_GET['status'] eq 0">selected="selected"</if>>正常</option>								<option value="1" <if condition="$_GET['status'] eq 1">selected="selected"</if>>关闭</option>							</select>														<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>商家名称</th>								<th>店铺名称</th>								<th>配送方式</th>								<th>商家电话</th>								<th>店铺电话</th>								<th>营业时间</th>								<th>创建时间</th>								<th>最后更新时间</th>								<th>店铺排序</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.merchant_name}</td>										<td>{pigcms{$vo.store_name}</td>										<td><if condition="$vo['deliver_type'] eq 0">{pigcms{$config['deliver_name']}<elseif condition="$vo['deliver_type'] eq 1" />商家配送<elseif condition="$vo['deliver_type'] eq 2" />自提<elseif condition="$vo['deliver_type'] eq 3" />{pigcms{$config['deliver_name']}或自提<elseif condition="$vo['deliver_type'] eq 4" />商家配送或自提</if></td>										<td>{pigcms{$vo.merchant_phone}</td>										<td>{pigcms{$vo.store_phone}</td>										<td>											<if condition="$vo['is_close']">												<font color="red">{pigcms{$vo.time}</font>											<else/>												{pigcms{$vo.time}											</if>										</td>										<td><if condition="$vo.create_time gt 0" >{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</if></td>										<td><if condition="$vo.last_time gt 0">{pigcms{$vo.last_time|date="Y-m-d H:i:s",###}<elseif condition="$vo.create_time gt 0" />{pigcms{$vo.create_time|date="Y-m-d H:i:s",###}</if></td>										<td>{pigcms{$vo.sort}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/edit_sort',array('store_id' => $vo['store_id']))}','设置店铺',560,400,true,false,false,editbtn,'edit',true);">设置店铺</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>