<include file="Public:header"/>	<div class="mainbox">		<div id="nav" class="mainnav_title">			<ul>				<a href="{pigcms{:U('Shop/sys_goods')}" class="on">商品列表</a>				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/goods_add')}','添加商品',600,420,true,false,false,addbtn,'add',true);">添加商品</a>				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/goods_import')}','添加商品',480,260,true,false,false,addbtn,'add',true);">导入商品</a>                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/unzip')}','上传图片zip压缩包',480,260,true,false,false,addbtn,'add',true);" >上传图片zip压缩包</a>			</ul>		</div>		<table class="search_table" width="100%">			<tr>				<td>					<form action="{pigcms{:U('Shop/sys_goods')}" method="get">						<input type="hidden" name="c" value="Shop"/>						<input type="hidden" name="a" value="sys_goods"/>
						搜索商品名称: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>						<select name="sort_id">							<option value="0" <if condition="$_GET['sort_id'] eq 0">selected="selected"</if>>全部分类</option>							<volist name="sort_list" id="sort">							<option value="{pigcms{$sort['sort_id']}" <if condition="$_GET['sort_id'] eq $sort['sort_id']">selected="selected"</if>>{pigcms{$sort['name']}</option>							</volist>						</select>						<input type="submit" value="查询" class="button"/>　					</form>				</td>			</tr>		</table>		<form name="myform" id="myform" action="" method="post">			<div class="table-list">				<table width="100%" cellspacing="0">					<colgroup>						<col/>						<col/>						<col/>						<col/>						<col/>						<col width="180" align="center"/>					</colgroup>					<thead>						<tr>							<th>商品编号</th>							<th>商品条形码</th>							<th>商品图片</th>							<th>商品名称</th>							<th>所属分类</th>							<th>商品单位</th>							<th>零售价(元)</th>							<th>进价(元)</th>							<th class="textcenter">操作</th>						</tr>					</thead>					<tbody>						<if condition="is_array($goods_list)">							<volist name="goods_list" id="vo">								<tr>									<td>{pigcms{$vo.goods_id}</td>									<td>{pigcms{$vo.number}</td>									<td><img src="{pigcms{$vo.image}" width="50" height="50" class="view_msg"></td>									<td>{pigcms{$vo.name}</td>									<td>{pigcms{$vo.sort_name}</td>									<td>{pigcms{$vo.unit}</td>									<td>{pigcms{$vo.price|floatval}</td>									<td>{pigcms{$vo.cost_price|floatval}</td>									<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/goods_edit',array('goods_id'=>$vo['goods_id'],'frame_show'=>true))}','查看商品信息',600,420,true,false,false,false,'detail',true);">查看</a> | 										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/goods_edit',array('goods_id'=>$vo['goods_id']))}','编辑商品信息',600,420,true,false,false,editbtn,'edit',true);">编辑</a> | 										<a href="javascript:void(0);" class="delete_row" parameter="goods_id={pigcms{$vo.goods_id}" url="{pigcms{:U('Shop/goods_del')}">删除</a>									</td>								</tr>							</volist>							<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>						<else/>							<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>						</if>					</tbody>				</table>			</div>		</form>	</div><include file="Public:footer"/>