<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Shop/sys_sort')}" class="on">分类列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/sort_add')}','添加分类',480,260,true,false,false,addbtn,'add',true);">添加分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>分类ID</th>								<th>排序</th>								<th>名称</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.sort_id}</td>										<td>{pigcms{$vo.sort}</td>										<td>{pigcms{$vo.name}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><else/><font color="red">关闭</font></if></td>										<td class="textcenter">    										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/sort_edit',array('sort_id'=>$vo['sort_id'],'frame_show'=>true))}','查看分类信息',480,260,true,false,false,false,'detail',true);">查看</a> |     										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Shop/sort_edit',array('sort_id'=>$vo['sort_id']))}','编辑分类信息',480,260,true,false,false,editbtn,'edit',true);">编辑</a> |     										<a href="javascript:void(0);" class="delete_row" parameter="sort_id={pigcms{$vo.sort_id}" url="{pigcms{:U('Shop/sort_del')}">删除</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="5">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="5">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>