<include file="Public:header"/>		<div class="mainbox">			<p>为了保证网页打开速度，同一分类最多只能添加20个店铺。</p>			<form class="frame_form" action="#" method="get">				<input type="hidden" name="c" value="Special"/>				<input type="hidden" name="a" value="choose_shop"/>				<input type="text" placeholder="请输入名称搜索词" value="" class="input input-image" name="search">				<button class="btnGrayS" style="height: 29px;">搜索</button>			</form>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>店铺名称</th>								<th>销量</th>								<th>配送类型</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$store_list">								<volist name="store_list" id="vo">									<tr>										<td>{pigcms{$vo.name}</td>										<td>{pigcms{$vo.sale_count}</td>										<td>											<switch name="vo['deliver_type']" >												<case value="0">{pigcms{$config['deliver_name']}</case>												<case value="1">商家配送</case>												<case value="2">客户自提</case>												<case value="3">{pigcms{$config['deliver_name']}或自提</case>												<case value="4">商家配送或自提</case>												<default />未知											</switch>										</td>										<td class="textcenter"><input type="button" class="button select_row product-{pigcms{$vo.store_id}" data-name="{pigcms{$vo.name}" data-image="{pigcms{$vo.image}" data-id="{pigcms{$vo.store_id}" value="选中"/></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="4">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="4">店铺列表为空</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript">			$(function(){				var product = window.top.frames['main'].get_selected_product();				for(var i in product){					$('.product-'+product[i]).val('已选择').prop('disabled',true);				}				$('.select_row').click(function(){					$(this).val('已选择').prop('disabled',true);					window.top.frames['main'].select_product($(this).data('id'),$(this).data('name'),$(this).data('image'));				});			});		</script><include file="Public:footer"/>