<include file="Public:header"/>		<div class="mainbox">			<p>为了保证网页打开速度，优惠券最多只能添加9个。</p>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>优惠券名称</th>								<th>优惠券金额</th>								<th>剩余数量</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="$coupon_list">								<volist name="coupon_list" id="vo">									<tr>										<td>{pigcms{$vo.name}</td>										<td>满{pigcms{$vo.order_money|floatval=###}减{pigcms{$vo.discount|floatval=###}元</td>										<td>{pigcms{$vo['num']-$vo['had_pull']}</td>										<td class="textcenter"><input type="button" class="button select_row coupon-{pigcms{$vo.coupon_id}" data-name="{pigcms{$vo.name}" data-discount="{pigcms{$vo.discount|floatval=###}" data-order_money="{pigcms{$vo.order_money|floatval=###}" data-id="{pigcms{$vo.coupon_id}" value="选中"/></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="4">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="4">优惠券列表为空</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript">			$(function(){				var coupon = window.top.frames['main'].get_selected_coupon();				for(var i in coupon){					$('.coupon-'+coupon[i]).val('已选择').prop('disabled',true);				}				$('.select_row').click(function(){					$(this).val('已选择').prop('disabled',true);					window.top.frames['main'].select_coupon($(this).data('id'),$(this).data('name'),$(this).data('discount'),$(this).data('order_money'));				});			});		</script><include file="Public:footer"/>