<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('shop')}" class="on">快店专题列表</a>|					<a href="{pigcms{:U('shop_add')}">创建专题页面</a>|				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>访问</th>								<th>二维码</th>								<th>访问量</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($special_list)">								<volist name="special_list" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td>{pigcms{$vo.name}</td>										<td><a href="{pigcms{$vo['url']}" target="_blank">访问预览</a></td>										<td><a href="{pigcms{$config.site_url}/wap.php?c=Special&id={pigcms{$vo.pigcms_id}" class="see_qrcode">查看二维码</a></td>										<td>{pigcms{$vo.hits}</td>										<td class="textcenter"><a href="{pigcms{:U('shop_edit',array('id'=>$vo['pigcms_id']))}">编辑</a>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.pigcms_id}" url="{pigcms{:U('del')}">删除</a></td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="4">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript">		$(function(){			$('.see_qrcode').click(function(){				var url = '{pigcms{$config.site_url}/index.php?c=Recognition&a=get_own_qrcode&qrCon=';				var qrcode_url = url+encodeURIComponent($(this).attr('href'));				parent.art.dialog.open(qrcode_url,{					init: function(){						var iframe = this.iframe.contentWindow;						window.top.art.dialog.data('iframe_handle',iframe);					},					id: 'handle',					title:'查看二维码',					padding: 0,					width: 315,					height: 315,					lock: true,					resize: false,					background:'black',					button: null,					fixed: false,					close: null,					left: '50%',					top: '38.2%',					opacity:'0.4'				});				return false;			});		});		</script><include file="Public:footer"/>