<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Spread/user_spread')}" >用户三级分佣记录</a>					<a href="{pigcms{:U('Spread/merchant_spread')}" class="on">商家推广分佣记录</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Spread/merchant_spread')}" method="get">							<input type="hidden" name="c" value="Spread"/>							<input type="hidden" name="a" value="merchant_spread"/>														搜索: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">																<option value="m_name" <if condition="$_GET['searchtype'] eq 'm_name'">selected="selected"</if>>商家名称</option>								<!--option value="mer_id" <if condition="$_GET['searchtype'] eq 'mer_id'">selected="selected"</if>>商家编号</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>商家电话</option-->							</select>							<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>														佣金类型筛选: 							<select id="order_type" name="order_type">									<option value="" <if condition="empty($_GET['order_type'])">selected="selected"</if>>全部</option>								<volist name="type_name" id="vo">									<option value="{pigcms{$key}" <if condition="$_GET['order_type'] eq $vo">selected="selected"</if>>{pigcms{$vo}</option>								</volist>																</select>							佣金状态筛选: 							<select id="status" name="status" >								<option value="0" <if condition="empty($_GET['status'])">selected="selected"</if>>全部</option>								<option value="1" <if condition="$_GET['status'] eq 1">selected="selected"</if>>已结算</option>								<option value="2" <if condition="$_GET['status'] eq 2">selected="selected"</if>>未结算</option>															</select>							<input type="submit" value="查询" class="button"/>　　						</form>					</td>					<td>						<a href="{pigcms{:U('Spread/export',$_GET)}&type=mer" class="button" style="float:right;margin-right: 10px;">导出订单</a>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col/>							<col width="100" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>获得佣金的商户</th>								<th>购买的用户</th>								<th>消费商家</th>								<th>订单类型</th>								<th>订单号</th>								<th>获得详情</th>								<th>金额</th>								<th>时间</th>							</tr>						</thead>						<tbody>							<if condition="is_array($list)">								<volist name="list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.name}</td>										<td><if condition="$vo.buy_nickname neq ''">{pigcms{$vo.buy_nickname}<else />{pigcms{$vo.buy_phone}</if></td>										<td>{pigcms{$vo.buy_mer_name}</td>										<td>{pigcms{$type_name[$vo['order_type']]}</td>										<td>{pigcms{$vo.order_id}</td>										<td>{pigcms{$vo.txt}</td>										<td>{pigcms{$vo.money}</td>																			<td>{pigcms{$vo['add_time']|date='Y-m-d H:i:s',###}</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>