<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>										<a href="{pigcms{:U('Sub_card/index')}" >免单套餐列表</a>|					<a href="{pigcms{:U('Sub_card/check_list',array('id'=>$_GET['id']))}" <if condition="$_GET['status'] eq 0">class="on"</if>>审核列表</a>								</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col width="100"/>							<col width="150"/>							<col width="300"/>							<col width="300"/>							<col width="150"/>							<col width="150"/>							<col width="180" align="center"/>						</colgroup>						<thead>							<tr>								<th>编号</th>								<th>参与的商家/店铺</th>								<th>描述</th>								<th>详细描述</th>								<th>图片(点击查看大图)</th>								<th>库存</th>								<th>参与时间</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($apply_list)">								<volist name="apply_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.mer_name}&nbsp;&nbsp;/&nbsp;&nbsp;{pigcms{$vo.store_name}</td>										<td>{pigcms{$vo.desc}</td>										<td>{pigcms{$vo.desc_txt|html_entity_decode}</td>										<td>											<if condition="is_array($vo['pic_list'])">											<volist name="vo['pic_list']" id="vv">												<img src="{pigcms{$vv}" style="width: 40px;height: 40px;margin-right:5px;"  class="view_msg">											</volist>											</if>										</td>										<td>{pigcms{$vo.sku}</td>										<td>{pigcms{$vo.apply_time|date="Y-m-d H:i:s",###}</td>										<td>																					<if condition="$vo['status'] eq 1">											<font color="green">已审核</font>																						<else />											<font color="red">未审核</font>											</if>										</td>										<td class="textcenter">										<if condition="$vo['status'] eq 1">											<a class="change_status"  style="cursor:pointer" url="{pigcms{:U('Sub_card/change_status')}"  parameter="id={pigcms{$vo['id']}&status=0&store_id={pigcms{$vo['store_id']}&sub_card_id={pigcms{$vo['sub_card_id']}" ><font color="black">取消资格</font></a>										<elseif condition="$sub_card.status eq 1" />											<a class="change_status"  style="cursor:pointer" url="{pigcms{:U('Sub_card/change_status')}"  parameter="id={pigcms{$vo['id']}&status=1&sub_card_id={pigcms{$vo['sub_card_id']}" ><font color="black">通过审核</font></a>										</if>										</td>									</tr>								</volist>									<tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="9">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>				<script>			$('.change_status').click(function(){				var now_dom = $(this);				window.top.art.dialog({					icon: 'question',					title: '请确认',					id: 'msg' + Math.random(),					lock: true,					fixed: true,					opacity:'0.4',					resize: false,					content: '你确定这样操作吗？' + (now_dom.attr('tip') ? '<br/><br/><div style="color:red;">'+now_dom.attr('tip')+'</div>' : ''),					ok:function (){						$.post(now_dom.attr('url'),now_dom.attr('parameter'),function(result){							if(result.status == 1){								window.top.msg(1,result.info,true);																window.location.reload();															}else{								window.top.msg(0,result.info);							}						});					},					cancel:true				});				return false;			});		</script><include file="Public:footer"/>