<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<a href="{pigcms{:U('cat_list')}" class="on">模板消息分类列表</a>			</div>			<div class="table-list">				<table width="100%" cellspacing="0">					<colgroup><col><col><col>  <col width="180" align="center"> </colgroup>					<thead>						<tr>							<th>分类编号</th>							<th>分类名称</th>							<th>分类备注</th>							<th>进入</th>						</tr>					</thead>					<tbody>						<volist name="cat_list" id="t">							<tr>								<td>{pigcms{$t.cat_id}</td>								<td>{pigcms{$t.cat_name}</td>								<td>{pigcms{$t.cat_label}</td>								<td><a href="{pigcms{:U('index',array('cat_id'=>$t['cat_id']))}">进入</a></td>							</tr>						</volist>					</tbody>				</table>			</div>		</div><include file="Public:footer"/>