<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<a href="{pigcms{:U('cat_list')}">模板消息分类列表</a>				<a href="{pigcms{:U('index',array('cat_id'=>$_GET['cat_id']))}" class="on">模板消息列表</a>			</div>			<div style="margin:20px 0 20px 10px;">每个分类中保证一个可用即可,若模板消息被微信删除时，我们会在该分类添加新的模板。</div>			<form name="myform" id="myform" action="{pigcms{:U('index')}" method="post" refresh="true">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col> 							<col>							<col>							<col>  							<col>  							<col>  							<col>  							<col width="180" align="center"> 						</colgroup>						<thead>							<tr>								<th>模板编号</th>								<th>模板名</th>								<th>回复内容</th>								<th>头部颜色</th>								<th>文字颜色</th>								<th>状态</th>								<th>模板ID</th>								<if condition="$config['pay_wxapp_appid']">									<th>小程序回复内容</th>									<th>小程序模板ID</th>								</if>							</tr>						</thead>						<tbody>							<volist name="list" id="t">			                    <tr>									<td>										<input type="hidden" name="cat_id[]" value="{pigcms{$t.cat_id}" />										<input type="hidden" name="tempkey[]" value="{pigcms{$t.tempkey}" />{pigcms{$t.tempkey}									</td>									<td>										<input type="hidden" name="name[]" value="{pigcms{$t.name}" />{pigcms{$t.name}									</td>									<td>										<input type="hidden" name="content[]" value="{pigcms{$t.content}" />										<pre>{pigcms{$t.content}</pre>									</td>									<td>										<input type="text" name="topcolor[]" value="{pigcms{$t.topcolor}" class="px color" style="width:55px;background:{pigcms{$t.topcolor};color:rgb(255,255,255);"/>									</td>									<td>										<input type="text" name="textcolor[]" value="{pigcms{$t.textcolor}" class="px color" style="width:55px;background:{pigcms{$t.textcolor};color:rgb(255,255,255);"/>									</td>									<td>										<select name="status[]">											<option value="0" <if condition="$t['status'] eq 0">selected</if>>关闭</option>											<option value="1" <if condition="$t['status'] eq 1">selected</if>>开启</option>										</select>									</td>									<td class="norightborder">										<input type="text" class="input-text" name="tempid[]" value="{pigcms{$t.tempid}" />										<if condition="empty($t['tempid'])">											<input type="button" value="获取模板ID" class="button1" data-id="{pigcms{$t.tempkey}"/>										</if>									</td>									<if condition="$config['pay_wxapp_appid']">										<td>											<if condition="$t['wxapp_content']">												<input type="hidden" name="wxapp_content[]" value="{pigcms{$t.wxapp_content}" />												<pre>{pigcms{$t.wxapp_content}</pre>											<else/>												无											</if>										</td>										<td class="norightborder">											<if condition="$t['wxapp_content']">												<input type="hidden" class="input-text" name="wxapp_id[]" value="{pigcms{$t.wxapp_id}" />												<input type="text" class="input-text wxapp_tempid" name="wxapp_tempid[]" value="{pigcms{$t.wxapp_tempid}" />												<if condition="empty($t['wxapp_tempid'])">													<input type="button" value="获取模板ID" class="button_wxapp" data-id="{pigcms{$t.tempkey}"/>												</if>											<else/>												无											</if>										</td>									</if>			                    </tr>							</volist>							<tr>								<td colspan="9" align="center"><input type="submit" name="dosubmit" value="保存" class="button"/></td>							</tr>						</tbody>					</table>				</div>			</form>		</div><script src="/static/js/cart/jscolor.js" type="text/javascript"></script><script type="text/javascript">
$(document).ready(function(){	var flag = false;	$('.button1').click(function(){		if (flag) return false;		flag = true;		var tempkey = $(this).attr('data-id'), obj = $(this).parent(".norightborder");		$.post('{pigcms{:U("TemplateMsg/getTemplateID")}', {'tempkey':tempkey}, function(response){			if (response.errcode){				alert(response.errmsg);			} else {				obj.find(".input-text").val(response.template_id);				$('#myform').submit();			}			flag = false;		}, 'json');	});	$('.button_wxapp').click(function(){		if (flag) return false;		flag = true;		var _this = $(this), tempkey = $(this).attr('data-id'), obj = $(this).parent(".norightborder");		$.post('{pigcms{:U("TemplateMsg/getWxappTemplateID")}', {'tempkey':tempkey}, function(response){			if (response.status == 0){				alert(response.info);			} else {				_this.remove();				obj.find(".wxapp_tempid").val(response.info);				$('#myform').submit();			}			flag = false;		}, 'json');	});});</script><include file="Public:footer"/>