<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Third_recharge/mobile_recharge')}" class="on">手机充值订单列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('mobile_recharge')}" method="get">							<input type="hidden" name="c" value="Third_recharge"/>							<input type="hidden" name="a" value="mobile_recharge"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<select name="searchtype">								<option value="uid" <if condition="$_GET['searchtype'] eq 'uid'">selected="selected"</if>>用户ID</option>								<option value="nickname" <if condition="$_GET['searchtype'] eq 'nickname'">selected="selected"</if>>昵称</option>								<option value="phone" <if condition="$_GET['searchtype'] eq 'phone'">selected="selected"</if>>手机号</option>							</select>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;							订单状态筛选: 							<select name="status">								<option value="-1" <if condition="!isset($_GET['status'])">selected</if>>全部</option>								<volist name="status" id="vo">									<option value="{pigcms{$key}" <if condition="$key eq $_GET['status']">selected</if>>{pigcms{$vo}</option>								</volist>							</select>							<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>							<input type="submit" value="查询" class="button"/>　　							<!--支付状态：							<select name="status" id="status">								<option value="-1" <if condition="$_GET['status'] eq -1">selected="selected"</if>>全部</option>								<option value="1" <if condition="$_GET['status'] eq 1">selected="selected"</if>>已支付</option>								<option value="0" <if condition="$_GET['status'] eq 0">selected="selected"</if>>未支付</option>							</select>-->						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>订单编号</th>								<th>手机号</th>								<th>充值金额</th>								<th>实际支付</th>								<th>充值用户</th>								<th>充值时间</th>								<th>充值状态</th>													</tr>						</thead>						<tbody>							<if condition="is_array($user_list)">								<volist name="user_list" id="vo">									<tr>										<td>{pigcms{$vo.order_id}</td>										<td>{pigcms{$vo.phone}</td>										<td><if condition="$vo.old_money eq 0">{pigcms{$vo.money}<else />{pigcms{$vo.old_money}</if></td>										<td>{pigcms{$vo.money}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo['add_time']|date='Y-m-d H:i:s',###}</td>										<td>												<if condition="$vo['status'] eq 0">												<span>未扣款</span>											<elseif condition="$vo['status'] eq 1" />												<span>扣款成功</span>											<elseif condition="$vo['status'] eq 2" />												<span>订单提交成功等待服务商充值</span>											<elseif condition="$vo['status'] eq 3" />												<span>充值中</span>											<elseif condition="$vo['status'] eq 4" />												<span>充值成功</span>											<elseif condition="$vo['status'] eq 5 OR $vo['status'] eq 6" />												<span>充值失败，已退款</span>											<elseif condition="$vo['status'] eq 12" />												<span>充值失败</span>											</if>																					</td>																			</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script>$(function(){	$('#status').change(function(){		location.href = "{pigcms{:U('User/recharge_list')}&status=" + $(this).val();	});});</script><include file="Public:footer"/>