<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('User/score_all_list')}" >积分记录列表</a>					<if condition="$config.open_score_fenrun eq 1"><a href="{pigcms{:U('User/fenrun_list')}" >分润记录列表</a></if>					<a href="{pigcms{:U('User/award_list')}" >佣金记录列表</a><a href="{pigcms{:U('User/free_award_list')}" >佣金解冻记录</a><a href="{pigcms{:U('User/system_fenrun_list')}" class="on">系统分润列表</a></if>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form name = "myform"action="{pigcms{:U('system_fenrun_list')}" method="get">							<input type="hidden" name="c" value="User"/>							<input type="hidden" name="a" value="system_fenrun_list"/>							筛选: 							<font color="#000">日期筛选：</font>							<input type="text" class="input-text" name="begin_time" style="width:120px;" id="d4311"  value="{pigcms{$_GET.begin_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>			   							<input type="text" class="input-text" name="end_time" style="width:120px;" id="d4311" value="{pigcms{$_GET.end_time}" onfocus="WdatePicker({readOnly:true,dateFmt:'yyyy-MM-dd'})"/>							<input type="submit" value="查询" class="button"/>　　							<a href="javascript:void(0)" onclick="fenrun_by_admin()" class="button">手动分润</a>							<if condition="$all_date.fenrun_yesterday eq true">【今日数据】<else />【昨天数据】</if>待分润积分总额：{pigcms{$all_date.all_score} 抽成总额：￥{pigcms{$all_date.all_money} 						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<style>					.table-list td{line-height:22px;padding-top:5px;padding-bottom:5px;}					</style>					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th>日期</th>								<th>积分数量</th>								<th>分润金额</th>								<th>昨天营收利润金额</th>								<th>时间</th>														</tr>						</thead>						<tbody>							<if condition="is_array($order_list)">								<volist name="order_list" id="vo">									<tr>										<td>{pigcms{$vo.id}</td>										<td>{pigcms{$vo.record_time|date='Y-m-d',### }</td>																		<td><font color="red">{pigcms{$vo.score_count|floatval}</font></td>										<td><font color="green">{pigcms{$vo.fenrun_money|floatval}</font></td>										<td><font color="blue">{pigcms{$vo.income|floatval}</font></td>																			<td>											{pigcms{$vo['add_time']|date='Y-m-d H:i:s',###}<br/>																				</td>																	</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><script type="text/javascript" src="{pigcms{$static_public}js/layer/layer.js"></script><script>var s;$(function(){	$('#status').change(function(){		location.href = "{pigcms{:U('User/recharge_list')}&status=" + $(this).val();	});	});	function fenrun_by_admin(){				layer.confirm('分润即将进行，请不要做任何操作，等待程序运行完成', {		  btn: ['确定','取消'] //按钮		}, function(){			layer.closeAll()			var url = "{pigcms{:U('User/fenrun_by_admin')}";			$.ajax({				url: url,				type: 'POST',				dataType: 'json',				data: '',				beforeSend:function(){					var index = layer.load(1, {						  shade: [0.3,'#000'] //0.1透明度的白色背景						});						$('.layui-layer').css('top','390px')				},				success:function(data){					layer.closeAll()					layer.alert(data.info, function(){					  window.location.reload();					});					$('.layui-layer').css('top','390px')									}			});		}, function(){		  window.location.reload();		  					});		$('.layui-layer').css('top','390px')	}		function check_percent(){		var url = "{pigcms{:U('User/ajax_get_fenrun_percent')}";		$.ajax({			url: url,			type: 'POST',			dataType: 'json',			data: '',			success:function(date){				if(date.status){					console.log(data.info)				}			}		});				s= setTimeout('check_percent()',3000)	}</script><include file="Public:footer"/>