<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<li><a class="on">活动列表</a></li>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th>活动来源</th>								<th>活动名称</th>								<th>活动图片</th>								<th>活动简介</th>								<th>活动类型</th>								<th>对接时间</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($wxapp_list)">								<volist name="wxapp_list" id="vo">									<tr>										<td>{pigcms{$vo.pigcms_id}</td>										<td><if condition="$vo['mer_id'] eq -1">平台<else /> 商家编号:{pigcms{$vo['mer_id']}</if></td>										<td>{pigcms{$vo.title}</td>										<td><img src="{pigcms{$vo.image}" width="50" height="50" class="view_msg"></td>										<td>{pigcms{$vo.info}</td>										<td>{pigcms{$vo.type_name}</td>										<td>{pigcms{$vo.add_time|date="Y-m-d H:i:s",###}</td>										<td class="status"><if condition="$vo['status']"><b style="color: green">正常</b><else /><b style="color:red">关闭</b></if></td>										<td class="textcenter">										<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Wxapp/edit',array('id'=>$vo['pigcms_id']))}','修改状态',480,260,true,false,false,editbtn,'edit',true);">编辑</a>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="13">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="13">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>