<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<a href="{pigcms{:U('Yuedan/agreement')}" class="on">协议列表</a>|			<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/agreement_add')}','添加协议',580,450,true,false,false,addbtn,'add',true);">添加协议</a>		</ul>	</div>	<if condition="!empty($_GET['cat_fid'])">		<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>	</if>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup>					<col/>					<col/>					<col/>					<col/>					<col/>					<col/>				</colgroup>				<thead>					<tr>						<th>编号</th>						<th>协议标题</th>					 	<th>协议标识</th>						<th>协议内容</th>						<th>添加时间</th>						<th class="textcenter">操作</th>					</tr>				</thead>				<tbody>					<if condition="is_array($agreementList)">						<volist name="agreementList" id="vo">							<tr>								<td>{pigcms{$vo.aid}</td>								<td>{pigcms{$vo.title}</td>								<td>{pigcms{$vo.key}</td>								<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/agreement_show',array('aid'=>$vo['aid'],'frame_show'=>true))}','查看协议内容',480,260,true,false,false,false,'detail',true);">查看</a></td>								<td>{pigcms{$vo.add_time|date="Y-m-d H:i:s",###}</td>								<td class="textcenter">									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/agreement_edit',array('aid'=>$vo['aid']))}','编辑分类信息',580,450,true,false,false,editbtn,'add',true);">编辑</a>  									<!-- <a href="javascript:void(0);" class="delete_row" parameter="aid={pigcms{$vo.aid}" url="{pigcms{:U('Yuedan/agreement_del')}">删除</a> -->								</td>							</tr>						</volist>					<else/>						<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>					</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>