<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Yuedan/authentication_config')}" class="on">字段列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/authentication_config_add')}','添加字段',600,400,true,false,false,addbtn,'add',true);">添加字段</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup>							<col/>							<col/>							<col/>							<col/>						</colgroup>						<thead>							<tr>								<th>名称</th>								<th>类型</th>								<th>唯一标记</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($authentication_config_list)">								<volist name="authentication_config_list" id="vo">									<tr>										<td>{pigcms{$vo.title}</td>										<td><if condition="$vo['type'] eq 1">文本<else/>图片</if></td>										<td>{pigcms{$vo.key}</td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/authentication_config_edit',array('acid'=>$vo['acid']))}','编辑字段',600,400,true,false,false,editbtn,'edit',true);">编辑</a>&nbsp;|&nbsp;										<a href="javascript:void(0);" class="delete_row" parameter="acid={pigcms{$vo.acid}" url="{pigcms{:U('Yuedan/authentication_config_del')}">删除</a>										</td>									</tr>								</volist>							<else/>								<tr><td class="textcenter red" colspan="4">表单填写项 列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>