<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<if condition="empty($now_category)">				<a href="{pigcms{:U('Yuedan/category')}" class="on">分类列表</a>|				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/cat_add',array('fcid'=>$fcid,'pfcid'=>$pfcid))}','添加主分类',580,450,true,false,false,addbtn,'add',true);">添加主分类</a>			<else/>				<a href="{pigcms{:U('Yuedan/category')}">分类列表</a>|				<a href="{pigcms{:U('Yuedan/category',array('fcid'=>$fcid))}" class="on">{pigcms{$now_category.cat_name} - 子分类列表</a>|				<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/cat_add',array('fcid'=>$fcid))}','添加子分类',580,450,true,false,false,addbtn,'add',true);">添加子分类</a> | 					<a href="{pigcms{:U('Yuedan/category',array('fcid'=>0,'pfcid'=>0))}">返回上级目录</a>			</if>		</ul>	</div>	<if condition="!empty($_GET['cat_fid'])">		<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>	</if>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup>					<col/>					<col/>					<col/>					<col/>					<col/>					<col/>				</colgroup>				<thead>					<tr>						<th>排序</th>						<th>分类ID</th>						<th>名称</th>						<if condition="$_GET['fcid'] lt 1">							<th>查看子分类</th>						</if>						<th>是否热门</th>					 	<if condition="$_GET['fcid'] gt 0">					 		<th>抽成比例</th>					 		<th>是否免费退款</th>					 		<th>违约金比例</th>					 		<th>退款时间（小时）</th>					 	</if>					 	<th>分类图标</th>					 	<if condition="$_GET['fcid'] lt 1">							<th>分类点击图标</th>						</if>						<th>状态</th>						<th class="textcenter">操作</th>					</tr>				</thead>				<tbody>					<if condition="is_array($category_list)">						<volist name="category_list" id="vo">							<tr>								<td>{pigcms{$vo.cat_sort}</td>								<td>{pigcms{$vo.cid}</td>								<td>{pigcms{$vo.cat_name}</td>								<if condition="$_GET['fcid'] lt 1">									<td><a href="{pigcms{:U('Yuedan/category',array('fcid'=>$vo['cid'],'pfcid'=>$vo['fcid']))}">查看子分类</a></td>								</if>								<td><if condition="$vo.is_hot eq 1"><font color="green">是</font><else/>否</if></td>								<if condition="$_GET['fcid'] gt 0">									<td>{pigcms{$vo.cut_proportion}</td>									<td><if condition="$vo.is_free eq 0"><font color="green">是</font><else/>否</if></td>									<td>{pigcms{$vo.cancel_proportion}</td>									<td>{pigcms{$vo.cancel_time} / 小时</td>							 	</if>							 	<td><img src="{pigcms{$vo.icon}" style="width: 40px; height: 40px;" alt=""></td>							 	<if condition="$_GET['fcid'] lt 1">									<td><img src="{pigcms{$vo.click_icon}" style="width: 40px; height: 40px;" alt=""></td>								</if>								<td><if condition="$vo['status'] eq 1"><font color="green">启用</font><elseif condition="$vo['status'] eq 2"/><font color="red">待审核</font><else/><font color="red">关闭</font></if></td>								<td class="textcenter">									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/cat_edit',array('cid'=>$vo['cid'],'frame_show'=>true))}','查看分类信息',580,450,true,false,false,false,'detail',true);">查看</a> | 									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/cat_edit',array('cid'=>$vo['cid']))}','编辑分类信息',580,450,true,false,false,editbtn,'add',true);">编辑</a> | 									<a href="javascript:void(0);" class="delete_row" parameter="cid={pigcms{$vo.cid}" url="{pigcms{:U('Yuedan/cat_del')}">删除</a>								</td>							</tr>						</volist>						<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>					<else/>						<tr><td class="textcenter red" colspan="11">列表为空！</td></tr>					</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>