<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Yuedan/examine')}" <if condition="$status eq 1">class="on"</if>>待审核列表</a>					<a href="{pigcms{:U('Yuedan/examine',array('status'=>2))}" <if condition="$status eq 2">class="on"</if>>已审核列表</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td style="width:50%;">						<form action="{pigcms{:U('Yuedan/examine')}" method="get">							<input type="hidden" name="c" value="Yuedan"/>							<input type="hidden" name="a" value="examine"/>							<input type="hidden" name="status" value="{pigcms{$_GET['status']}"/>							筛选: 							<if condition="$admin_area neq 3">								<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>							</if>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}"/>							<input type="submit" value="查询" class="button"/>						</form>					</td>				</tr>			</table>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th width="200">服务标题</th>								<th>用户</th>								<th>添加时间</th>								<th>服务位置</th>								<th>价格/单位</th>								<th>所属分类</th>								<th>审核状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($service_list)">								<volist name="service_list" id="vo">									<tr>										<td>{pigcms{$vo.rid}</td>										<td>{pigcms{$vo.title}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td>{pigcms{$vo.address_name}</td>										<td>{pigcms{$vo.price}/{pigcms{$vo.unit}</td>										<td>{pigcms{$vo.cat_name}</td>										<td><if condition="$vo['status'] eq 1"><font color="red">审核中</font><elseif condition="$vo['status'] eq 3"/><font color="red">已拒绝</font><elseif condition="$vo['status'] eq 2"/><font color="green">已通过</font></if></td>										<if condition="$vo['status'] eq 1">											<td class="textcenter">												<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/examine_edit', array('rid'=>$vo['rid']))}','技能审核',680,560,true,false,false,editbtn,'edit',true);">审核</a>											</td>										<else/>											<td class="textcenter">												<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/examine_show', array('rid'=>$vo['rid'],'statuss'=>1))}','审核详情',680,560,true,false,false);">查看</a>&nbsp;|&nbsp;<a href="javascript:void(0);" class="delete_row" parameter="rid={pigcms{$vo.rid}" url="{pigcms{:U('Yuedan/service_release_del')}">删除</a>											</td>										</if>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script><include file="Public:footer"/>