<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<a href="{pigcms{:U('Yuedan/grade')}" class="on">等级列表</a>|			<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/grade_add')}','添加等级',600,300,true,false,false,addbtn,'store_add',true);">添加等级</a>|			<a href="{pigcms{:U('Yuedan/grade_order')}">等级订单</a>|		</ul>	</div>	<div class="table-list">		<table width="100%" cellspacing="0">			<colgroup>				<col/>				<col/>				<col/>				<col/>				<col width="180" align="center"/>			</colgroup>			<thead>				<tr>					<th>等级</th>					<th>购买金额</th>					<th>抽成比例</th>					<th class="textcenter">操作</th>				</tr>			</thead>			<tbody>				<if condition="is_array($gradeList)">					<volist name="gradeList" id="vo">						<tr>							<td>{pigcms{$vo.grade}</td>							<td>{pigcms{$vo.money}</td>							<td>{pigcms{$vo.precent}</td>							<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/grade_edit',array('grade_id'=>$vo['grade_id']))}','编辑',600,400,true,false,false,editbtn,'edit',true);">编辑</a></td>						</tr>					</volist>				<else/>					<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>				</if>			</tbody>		</table>	</div></div><include file="Public:footer"/>