<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Yuedan/grade')}">等级列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/grade_add')}','添加等级',600,300,true,false,false,addbtn,'store_add',true);">添加等级</a>|					<a href="{pigcms{:U('Yuedan/grade_order')}" class="on">等级订单</a>|				</ul>			</div>						<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th width="200">支付金额</th>								<th>等级金额</th>								<th>添加时间</th>								<th>状态</th>							</tr>						</thead>						<tbody>							<if condition="is_array($gradeList)">								<volist name="gradeList" id="vo">									<tr>										<td>{pigcms{$vo.grade_order_id}</td>										<td>{pigcms{$vo.pay_money}</td>										<td>{pigcms{$vo.grade_money}</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td><if condition="$vo['status'] eq 1"><font color="green">正常</font><elseif condition="$vo['status'] eq 3"/><font color="red">已拒绝</font><elseif condition="$vo['status'] eq 2"/><font color="green">已通过</font></if></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="11">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>		<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script><include file="Public:footer"/>