<include file="Public:header"/><div class="mainbox">	<div id="nav" class="mainnav_title">		<ul>			<a href="{pigcms{:U('Yuedan/handbook')}" class="on">约单手册</a>|			<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/handbook_add')}','添加手册',800,570,true,false,false,addbtn,'add',true);">添加手册</a>		</ul>	</div>	<if condition="!empty($_GET['cat_fid'])">		<div style="height:30px;line-height:30px;">提示：若主分类下只有一个子分类，网站上子分类不会显示。</div>	</if>	<form name="myform" id="myform" action="" method="post">		<div class="table-list">			<table width="100%" cellspacing="0">				<colgroup>					<col/>					<col/>					<col/>					<col/>					<col/>					<col/>				</colgroup>				<thead>					<tr>						<th>编号</th>						<th>标题</th>						<th>内容</th>						<th>添加时间</th>						<th>是否显示</th>						<th class="textcenter">操作</th>					</tr>				</thead>				<tbody>					<if condition="is_array($handbook_list)">						<volist name="handbook_list" id="vo">							<tr>								<td>{pigcms{$vo.handbook_id}</td>								<td>{pigcms{$vo.title}</td>								<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/handbook_show', array('handbook_id'=>$vo['handbook_id'],'statuss'=>1))}','手册内容',680,560,true,false,false);">查看</a></td>								<td>{pigcms{$vo.add_time|date="Y-m-d H:i:s",###}</td>								<td>{pigcms{$vo.is_show}</td>								<td class="textcenter">									<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/handbook_edit',array('handbook_id'=>$vo['handbook_id']))}','编辑分类信息',800,570,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="handbook_id={pigcms{$vo.handbook_id}" url="{pigcms{:U('Yuedan/handbook_del')}">删除</a>								</td>							</tr>						</volist>						<tr><td class="textcenter pagebar" colspan="6">{pigcms{$pagebar}</td></tr>					<else/>						<tr><td class="textcenter red" colspan="6">列表为空！</td></tr>					</if>				</tbody>			</table>		</div>	</form></div><include file="Public:footer"/>