<include file="Public:header"/><form id="myform" method="post" action="{pigcms{:U('Yuedan/handbook_edit')}" enctype="multipart/form-data">    <table cellpadding="0" cellspacing="0" class="frame_form" width="100%">        <tr>            <th width="80">手册标题</th>            <td><input type="text" class="input fl" name="title" value="{pigcms{$handbook_info.title}" size="75" placeholder="手册标题" validate="maxlength:50,required:true"/></td>        </tr>        <tr>            <th width="90">是否显示</th>            <td>                <span class="cb-enable"><label class="cb-enable <if condition="$handbook_info['is_show'] eq 1">selected</if>"><span>显示</span><input type="radio" name="is_show" value="1" <if condition="$handbook_info['is_show'] eq 1">checked="checked"</if>/></label></span>                <span class="cb-disable"><label class="cb-disable <if condition="$handbook_info['is_show'] eq 0">selected</if>"><span>关闭</span><input type="radio" name="is_show" value="0" <if condition="$handbook_info['is_show'] eq 0">checked="checked"</if>/></label></span>            </td>        </tr>        <tr>            <th width="80">详细内容</th>            <td>                <textarea name="content" id="content">{pigcms{$handbook_info.content}</textarea>            </td>        </tr>    </table>    <div class="btn hidden">        <input type="hidden" name="handbook_id" value="{pigcms{$handbook_info.handbook_id}">        <input type="submit" name="dosubmit" id="dosubmit" value="提交" class="button" />        <input type="reset" value="取消" class="button" />    </div></form><script src="{pigcms{$static_public}kindeditor/kindeditor.js"></script><script type="text/javascript">    KindEditor.ready(function(K){        kind_editor = K.create("#content",{            width:'450px',            height:'450px',            resizeType : 1,            allowPreviewEmoticons:false,            allowImageUpload : true,            filterMode: true,            items : [                'source', 'fullscreen', '|', 'fontname', 'fontsize', '|', 'forecolor', 'hilitecolor', 'bold', 'italic', 'underline',                'removeformat', '|', 'justifyleft', 'justifycenter', 'justifyright', 'insertorderedlist',                'insertunorderedlist', '|', 'emoticons', 'image', 'link'            ],            emoticonsPath : './static/emoticons/',            uploadJson : "{pigcms{$config.site_url}/index.php?g=Index&c=Upload&a=editor_ajax_upload&upload_dir=fc/school"        });    });</script><include file="Public:footer"/>