<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Yuedan/order')}" class="on">订单列表</a>				</ul>			</div>			<if condition="$admin_area neq 3">				<table class="search_table" width="100%">					<tr>						<td style="width:50%;">							<form action="{pigcms{:U('Yuedan/order')}" method="get">								<input type="hidden" name="c" value="Yuedan"/>								<input type="hidden" name="a" value="order"/>								筛选: 									<div id="choose_pca" province_idss="{pigcms{$_GET.province_idss}" city_idss="{pigcms{$_GET.city_idss}" area_id="{pigcms{$_GET.area_id}" style="display:inline"></div>																<input type="submit" value="查询" class="button"/>							</form>						</td>					</tr>				</table>			</if>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>编号</th>								<th width="150px;">服务标题</th>								<th>购买用户</th>								<th>下单时间</th>								<th>订单总价</th>								<th>价格/单位</th>								<th>所属分类</th>								<th>留言记录</th>								<th>订单状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($orderList)">								<volist name="orderList" id="vo">									<tr>										<td>{pigcms{$vo.order_id}</td>										<td>{pigcms{$vo.title}</td>										<td>{pigcms{$vo.nickname}</td>										<td>{pigcms{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td>{pigcms{$vo.total_price}</td>										<td>{pigcms{$vo.price}/{pigcms{$vo.unit}</td>										<td>{pigcms{$vo.cat_name}</td>										<td><a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Yuedan/order_message',array('order_id'=>$vo['order_id'],'frame_show'=>true))}','查看订单交流记录',580,450,true,false,false,false,'detail',true);">查看</a></td>										<!-- '订单状态 1正常，2已支付，3以服务，4订单完成，5完成已评价，6已关闭', -->										<td>											<if condition="$vo['status'] eq 1">												<font color="red">待支付</font>											<elseif condition="$vo['status'] eq 2"/>												<font color="red">已支付</font>											<elseif condition="$vo['status'] eq 3"/>												<font color="green">已服务</font>											<elseif condition="$vo['status'] eq 4"/>												<font color="green">订单完成</font>											<elseif condition="$vo['status'] eq 5"/>												<font color="green">订单已评价</font>											<elseif condition="$vo['status'] eq 6"/>												<font color="green">订单已取消</font>											<elseif condition="$vo['status'] eq 7"/>												<font color="green">服务中</font>											<elseif condition="$vo['status'] eq 7"/>												<font color="green">申请退款中</font>											</if>										</td>										<td class="textcenter">											<if condition="$vo['status'] eq 1">												<font color="red">--</font>											<elseif condition="$vo['status'] eq 2"/>												<font color="red">--</font>											<elseif condition="$vo['status'] eq 3"/>												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.order_id}")'>服务退款</a> |												<a href="javascript:void(0);" onclick='orderConfirm("{pigcms{$vo.order_id}")'>完成服务</a>											<elseif condition="$vo['status'] eq 4"/>												<font color="green">--</font>											<elseif condition="$vo['status'] eq 5"/>												<font color="green">--</font>											<elseif condition="$vo['status'] eq 6"/>												<font color="green">--</font>											<elseif condition="$vo['status'] eq 7"/>												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.order_id}")'>服务退款</a>											<elseif condition="$vo['status'] eq 8"/>												<a href="javascript:void(0);" onclick='refundReply("{pigcms{$vo.order_id}")'>服务退款</a>											</if>										</td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="10">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="10">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div>	<script type="text/javascript" src="{pigcms{$static_path}js/area_pca.js"></script>		<script>			function refundReply(order_id){                var refund_reply_url = "{pigcms{:U('Yuedan/refund_reply')}";                var status = 6;                $.post(refund_reply_url,{'order_id':order_id,'status':status},function(data){                    if(data.error == 1){                        alert(data.msg);                        location.reload();                    }else{                        alert(data.msg);                    }                },'json')            }		</script><include file="Public:footer"/>